INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000031, 'Агнешко на фурна', '/static/images/dishes/agneshkopecheno.jpg', '["агнешко месо - 2 кг или агнешко бутче", "лук - 2 връзки пресен", "джоджен - 5 - 6 стръка пресен", "черен пипер", "сол", "червен пипер"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000031, 'Агнешко на фурна', 200, '/static/images/dishes/agneshkopecheno.jpg', '[{"name": "агнешко месо - 2 кг или агнешко бутче", "imageUrl": "/static/images/dishes/agneshko.jpg"}, {"name": "лук - 2 връзки пресен", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "джоджен - 5 - 6 стръка пресен", "imageUrl": "/static/images/dishes/djodjen.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "червен пипер", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}]', '["Измийте внимателно агнешкото месо (бутче), подсушете го и го натрийте със сол, черен и червен пипер. Оставете го за 30 минути на студено, за да поеме аромата на подправките.", "В това време нарежете лука и джоджена на дребно. Сложете ги в дълбока тава, залейте с 1 ч.ч. топла вода. Добавете месото и запечете в предварително загрята на 200 градуса фурна, докато месото хване апетитна коричка.", "След това покрийте плътно с фолио и печете поне 3 часа на 180 градуса, като след два часа печене трябва да проверите дали няма нужда от доливане на още малко вода.", "Малко преди да извадите месото от фурната трябва да махнете фолиото, за да се запече и хване хрупкава коричка. Поднесете печеното агнешко със задушени картофи или друга гарнитура по избор."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000054, 'Супа с агнешко месо и фиде', '/static/images/dishes/agneshkas.jpg', '["агнешко месо - крачета", "чушки - 1 бр.", "домати - 2 бр.", "лук - 1 глава кромид", "чубрица", "фиде", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000054, 'Супа с агнешко месо и фиде', 120, '/static/images/dishes/agneshkas.jpg', '[{"name": "агнешко месо - крачета", "imageUrl": "/static/images/dishes/agneshko_meso.jpg"}, {"name": "чушки - 1 бр.", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "домати - 2 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "лук - 1 глава кромид", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "чубрица", "imageUrl": "/static/images/dishes/chubrica.jpg"}, {"name": "фиде", "imageUrl": "/static/images/dishes/фиде.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Агнешките крачета се слагат с кокалите цели да се сварят в подсолена вода. След като станат ги вадим и ги слагаме да поизстинат.", "Обезкостяваме ги с помощта на кухненска ножица, нарязваме месото на малки парченца.", "Вземаме тенджерата с бульона, в който сме варили крачетата, и го слагаме на огъня. Нарязваме лука и чушките и ги слагаме се варят.", "Добавяме и олио, като покипи 10 минути прибавяме месото и фидето (около 2 шепи) и оставяме да супата да се вари 15 минути, след това слагаме и нарязаните домати.", "Посоляваме и слагаме чубрица на вкус."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000004, 'Бабините кюфтета с бял сос', '/static/images/dishes/pile_frikase.jpg', '["яйца - 4 бр.", "свинска кайма - 500 г", "ориз - 100 г", "кисело мляко - 1 кофичка", "брашно - 3 - 4 с.л.", "черен пипер - 2 г", "сол", "магданоз - пресен"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000004, 'Бабините кюфтета с бял сос', 70, '/static/images/dishes/pile_frikase.jpg', '[{"name": "яйца - 4 бр.", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "свинска кайма - 500 г", "imageUrl": "/static/images/dishes/kaima.jpg"}, {"name": "ориз - 100 г", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "кисело мляко - 1 кофичка", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "брашно - 3 - 4 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "черен пипер - 2 г", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "магданоз - пресен", "imageUrl": "/static/images/dishes/magdanos.jpg"}]', '["Смесваме каймата с лука и магданоза, добавяме ориза, яйцето и чубрицата и омесваме.", "Оваляме кюфтетата в брашно и ги пускаме в тенджера с вряща подсолена вода. Варим до готовност.", "В дълбока купа разбъркваме останалите яйца, млякото и брашното. Гребваме от бульона, в който са врели кюфтетата, и смесваме бавно, за да не се пресекат яйцата.", "Връщаме соса в тенджерата при непрекъснато бъркане и оставяме да поври още 10 минути, докато се получи гъст сос.", "Гарнираме със ситно нарязан магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000079, 'Баклава с орехи', '/static/images/dishes/baklava.jpg', '["кори - 1 кг", "захар - 6 ч.ч.", "орехи - 400 г смлени", "масло - 625 г (5 малки пакетчета)", "лимони - 1 бр."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000079, 'Баклава с орехи', 75, '/static/images/dishes/baklava.jpg', '[{"name": "кори - 1 кг", "imageUrl": "/static/images/dishes/kori.jpg"}, {"name": "захар - 6 ч.ч.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "орехи - 400 г смлени", "imageUrl": "/static/images/dishes/orehi.jpg"}, {"name": "масло - 625 г (5 малки пакетчета)", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "лимони - 1 бр.", "imageUrl": "/static/images/dishes/limoni.jpg"}]', '["В намазнена тавичка с масло внимателно се нареждат корите, една върху друга, без никаква мазнина помежду им. Орехите се разпределят по равно, на две места, в средната част (не най-отгоре).", "Разрязва се на ромбоидни парченца. Маслото се разтопява (без да завира) и се залива равномерно докато е вряло, върху баклавата. Пече се в умерена фурна, до златисто. Оставя се да изстине напълно.", "Приготвя се сироп от 6-те чаши захар и 4 чаши вода, който трябва да ври 5 минути, като постоянно се бърка. Накрая към сиропа се добавя сока на един лимон и малко настъргана лимонена кора.", "Баклавата се залива и се остава една нощ, да попие добре."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (20000016, 'Баница с готови кори и сирене', '/static/images/dishes/banica.jpg', '["кори за баница - 1 пакет (500 г)", "сирене - 300 - 400 г", "кисело мляко - 1 кофичка (400 г)", "брашно - 2 с.л.", "сода бикарбонат - 1/2 ч.л.", "олио - за помазване", "яйца - 3 бр."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (20000016, 'Баница с готови кори и сирене', 45, '/static/images/dishes/banica.jpg', '[{"name": "кори за баница - 1 пакет (500 г)", "imageUrl": "/static/images/dishes/kori.jpg"}, {"name": "сирене - 300 - 400 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "кисело мляко - 1 кофичка (400 г)", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "брашно - 2 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "сода бикарбонат - 1/2 ч.л.", "imageUrl": "/static/images/dishes/sodabikarbonat.jpg"}, {"name": "олио - за помазване", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "яйца - 3 бр.", "imageUrl": "/static/images/dishes/qica.jpg"}]', '["Яйцата, сиренето, киселото мляко, брашното и содата се объркват в купа до получаване на сместа за плънката.", "Корите за баница се вадят две по две. Първата се помазва с олио, а втората - със сместа. Навиват се и последователно се поставят в предварително помазана с олио тава до оформянето на баницата.", "В случай че остане от сместа, се изсипва отгоре и се помазва с него, а в случай че не остане - помазва се с олио", "Печете баницата до зачервяване в предваритело загрята фурна на 200 градуса.", "Получава се страхотна традиционна вита баница!"]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000037, 'Търновска бобена салата', '/static/images/dishes/bobena.jpg', '["боб - 300 г", "лук - 4 глави", "червен пипер - 2 с.л.", "черен пипер", "сол", "олио"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000037, 'Търновска бобена салата', 840, '/static/images/dishes/bobena.jpg', '[{"name": "боб - 300 г", "imageUrl": "/static/images/dishes/bob.jpg"}, {"name": "лук - 4 глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "червен пипер - 2 с.л.", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["За тази бобен салата по Великотърновски бобът се измива много добре и се накисва в студена вода за 12 часа, като водата, която покрива боба, трябва да се достъпно количество, за да не набъбне и остане сух.", "След 12 часа се измива много добре и се залива със студена вода. Слага се да заври, след което се изсипва водата и се залива отново със студена вода.            ", "Процедурата се повтаря три пъти. По време на варенето се отстранява пяната, която бобът отделя, като не се отстранява с решетъчна лъжица, а с лъжица без дупки, за да се отстрани цялата пяна.", "Бобът се вари с четвъртата вода до пълното сваряване.", "След сваряването бобът се отцежда много добре.", "Лукът за апетитната бобена салата се почиства, измива и нарязва на полумесеци. Слага се в тиган с незалепващо покритие и се пържи в олио до омекване.", "Прибавя се отцеденият боб, като пърженето продължава 5 мин, след което се слага червеният пипер, черен пипер и сол.", "Пърженето продължава на среден огън 10 мин.", "Оформете Търновската бобена салата красиво в чинии и направете украса по желание."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000080, 'Бисквитена торта ', '/static/images/dishes/biskvitena.jpg', '["бисквити - 5 пакета обикновени с масло", "нишесте - 2 броя с вкус на ванилия", "прясно мляко - 1, 2 литра", "захар - 5 с.л.", "захар - 5 с.л.", "орехи - за поръсване"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000080, 'Бисквитена торта ', 50, '/static/images/dishes/biskvitena.jpg', '[{"name": "бисквити - 5 пакета обикновени с масло", "imageUrl": "/static/images/dishes/biskviti.jpg"}, {"name": "нишесте - 2 броя с вкус на ванилия", "imageUrl": "/static/images/dishes/nisheste.jpg"}, {"name": "прясно мляко - 1, 2 литра", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "захар - 5 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "захар - 5 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "орехи - за поръсване", "imageUrl": "/static/images/dishes/orehi.jpg"}]', '["Разтворете нишестето, сложете прясното мляко, подсладено със захарта да кипне и направете крема.", "Започнете да редите тортата в правоъгълна тава, като на дъното полеете малко крем, след това ред бисквити, отново крем, ред банани, ред бисквити и т.н.", "Най-отгоре полейте обилно с останалия крем и поръсете с наситнени орехи.            ", "Оставете тортата да престои 6 часа преди да сервирате."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000002, 'Bоб чорба', '/static/images/dishes/bob_chorba1.jpg', '["боб - 500 г", "зелен лук - 2 стръка", "лук - 1 глава", "моркови - 1 бр.", "чесън - 2 скилидки", "домати - 2 бр", "олио", "червен пипер - 1 с.л.", "черен пипер", "чубрица", "джоджен", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000002, 'Bоб чорба', 165, '/static/images/dishes/bob_chorba1.jpg', '[{"name": "боб - 500 г", "imageUrl": "/static/images/dishes/bob.jpg"}, {"name": "зелен лук - 2 стръка", "imageUrl": "/static/images/dishes/praz.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 1 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "чесън - 2 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "домати - 2 бр", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "червен пипер - 1 с.л.", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "чубрица", "imageUrl": "/static/images/dishes/chubrica.jpg"}, {"name": "джоджен", "imageUrl": "/static/images/dishes/djodjen.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Бобът се слага в тенджера. Сипва се вода и се оставя да се кисне 1 нощ.", "На боба се сменя водата и се слага да се вари. Водата се сменя и отново се завира. Вари се около 90 минути.", "Лукът се почиства и се нарязва на ситно. Морковът се обелва и измива. Настъргва се. Доматите се измиват и нарязват на кубченца.", "Челядинките се доизмиват. Чесънът се обелва и нарязва на едро.", "Всички зеленчуци се слагат в тенджерата.", "В малко от бульона се размива червеният пипер. Добавя се към боб чорбата, както и олио. Добавят се чубрица и джоджен.Подправя се със сол и черен пипер. ", "Оставя се да се вари до сваряване на всички зеленчуци и до пълна готовност на боба.", "Насладете се на купа лесна боб чорба."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000001, 'Лесна боб чорба с челядинки', '/static/images/dishes/bob_chorba.jpg', '["боб - 500 г", "челядинки - 250 г", "лук - 1 глава", "моркови - 1 бр.", "чесън - 2 скилидки", "домати - 2 бр", "олио", "червен пипер - 1 с.л.", "черен пипер", "чубрица", "джоджен", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000001, 'Лесна боб чорба с челядинки', 165, '/static/images/dishes/bob_chorba.jpg', '[{"name": "боб - 500 г", "imageUrl": "/static/images/dishes/bob.jpg"}, {"name": "челядинки - 250 г", "imageUrl": "/static/images/dishes/chelqdinki.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 1 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "чесън - 2 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "домати - 2 бр", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "червен пипер - 1 с.л.", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "чубрица", "imageUrl": "/static/images/dishes/chubrica.jpg"}, {"name": "джоджен", "imageUrl": "/static/images/dishes/djodjen.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Бобът се слага в тенджера. Сипва се вода и се оставя да се кисне 1 нощ.", "На следващия ден челядинките се накисват във вода.", "На боба се сменя водата и се слага да се вари. Водата се сменя и отново се завира. Вари се около 90 минути.", "Лукът се почиства и се нарязва на ситно. Морковът се обелва и измива. Настъргва се. Доматите се измиват и нарязват на кубченца.", "Челядинките се доизмиват. Чесънът се обелва и нарязва на едро.", "Всички зеленчуци се слагат в тенджерата.", "В малко от бульона се размива червеният пипер. Добавя се към боб чорбата, както и олио. Добавят се чубрица и джоджен.Подправя се със сол и черен пипер. ", "Оставя се да се вари до сваряване на всички зеленчуци и до пълна готовност на боба.", "Насладете се на купа лесна боб чорба с челядинки."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000073, 'Бухтички с пудра захар', '/static/images/dishes/buhti.jpg', '["яйца - 2 бр.", "кисело мляко - 1/2 кофичка", "сода за хляб - 1 ч.л.", "сол - 1 малка щипка", "захар - 5 с.л.", "пудра захар - за поръсване", "олио - 2 с.л.", "олио - за пържене.", "брашно - 500 - 600 г"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000073, 'Бухтички с пудра захар', 40, '/static/images/dishes/buhti.jpg', '[{"name": "яйца - 2 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "кисело мляко - 1/2 кофичка", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "сода за хляб - 1 ч.л.", "imageUrl": "/static/images/dishes/soda.jpg"}, {"name": "сол - 1 малка щипка", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "захар - 5 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "пудра захар - за поръсване", "imageUrl": "/static/images/dishes/pudra.jpg"}, {"name": "олио - 2 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "олио - за пържене.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "брашно - 500 - 600 г", "imageUrl": "/static/images/dishes/brashno.jpg"}]', '["Содата се разтваря в киселото мляко, добавят се разбитите яйца, захарта, солта и олиото.", "Добавя се пресятото брашно и се замесва тесто. Оставя се да си почине 10 минути.", "Тестото се разточва на кора, изрязват се формички и се пържат в сгорещено олио.", "Топлите бухти се поръсват с пудра захар.", "Поднесете Бухтичките с пудра захар за закуска."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000035, 'Бърза салата с домати и краставици', '/static/images/dishes/burkana.jpg', '["домати - 2 бр.", "краставици - 1 бр.", "лук - 2 бр.", "олио", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000035, 'Бърза салата с домати и краставици', 10, '/static/images/dishes/burkana.jpg', '[{"name": "домати - 2 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 1 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "лук - 2 бр.", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Доматите, краставиците и лукът се измиват и нарязват.", "Овкусяват се със сол и олио."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000017, 'Традиционна българска мусака', '/static/images/dishes/carska_turshia.jpg', '["зелени домати - 1 кг", "моркови - 1 кг", "червени камби - 1 кг", "зелени чушки - 1 кг", "червени чушки - 1 кг", "карфиол - 1 кг", "зеле - 3 кг", "олио - 40 мл.", "вода - 2 л", "оцет - 1 л", "сол - 300 г", "захар - 500 г", "черен пипер - 1 ч.л. зърна", "аспирин - 3 бр на буркан"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000017, 'Традиционна българска мусака', 120, '/static/images/dishes/carska_turshia.jpg', '[{"name": "зелени домати - 1 кг", "imageUrl": "/static/images/dishes/zeleni_domati.jpg"}, {"name": "моркови - 1 кг", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "червени камби - 1 кг", "imageUrl": "/static/images/dishes/chushka.jpg"}, {"name": "зелени чушки - 1 кг", "imageUrl": "/static/images/dishes/chushka.jpg"}, {"name": "червени чушки - 1 кг", "imageUrl": "/static/images/dishes/chushka.jpg"}, {"name": "карфиол - 1 кг", "imageUrl": "/static/images/dishes/karfiol.jpg"}, {"name": "зеле - 3 кг", "imageUrl": "/static/images/dishes/zele.jpg"}, {"name": "олио - 40 мл.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "вода - 2 л", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "оцет - 1 л", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "сол - 300 г", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "захар - 500 г", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "черен пипер - 1 ч.л. зърна", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "аспирин - 3 бр на буркан", "imageUrl": "/static/images/dishes/aspirin.jpg"}]', '["Зеленчуците се измиват, почистват и нарязват на дребно, поставят се в дълбок съд и се заливат с маринатата, която се приготвя чрез смесване на посочените продукти и подправки.", "Маринатата не се вари на този етап. Съдът се поставя на студено, да престои 24 часа, като периодично се разбърква.", "След това зеленчуците се отцеждат и подреждат в подготвените буркани. Маринатата се кипва, отдръпва се от огъня и след като се охлади, се налива върху зеленчуците в бурканите.", "Но преди това за всеки буркан предварително и поотделно с малко от изстиналата марината се стриват по 3 бр. аспирин, като тази бройка се отнася за компотени буркани или омния, както мисля, че ги наричат.", "Царската туршия не се вари, оставя се на студено, а зимата се сервира с ракийка или по ваш вкус.", "Забележка: Ако ви се струва доста количеството /10 кг продукти/, то може лесно да приготвите по-малко количество и в съотношение според предпочитаните ви продукти, като лесно ще изчислите, че за 1 кг нарязани зеленчуци ще е необходимо 200 мл вода, 100 мл оцет, 30 г сол, 50 г захар, лъжичка чаена олио."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000026, 'Салата с пилешко филе и орехи - Цезар', '/static/images/dishes/cezar.jpg', '["айсберг салата", "пилешко месо - 150 г", "чери домати - 6 - 7 бр.", "краставици - 1 бр.", "орехи", "пармезан", "мед", "лимони", "зехтин", "сол", "черен пипер"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000026, 'Салата с пилешко филе и орехи - Цезар', 20, '/static/images/dishes/cezar.jpg', '[{"name": "айсберг салата", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "пилешко месо - 150 г", "imageUrl": "/static/images/dishes/pileshko.jpg"}, {"name": "чери домати - 6 - 7 бр.", "imageUrl": "/static/images/dishes/cheryy_domati.jpg"}, {"name": "краставици - 1 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "орехи", "imageUrl": "/static/images/dishes/orehi.jpg"}, {"name": "пармезан", "imageUrl": "/static/images/dishes/parmezan.jpg"}, {"name": "мед", "imageUrl": "/static/images/dishes/med.jpg"}, {"name": "лимони", "imageUrl": "/static/images/dishes/limoni.jpg"}, {"name": "зехтин", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}]', '["Айсберг салатата накъсваме на средно големи парчета.", "Краставичката и доматите режем на малки парчета и ги слагаме в средно голяма купа.", "Пилешкото месо овкусяваме със сол и черен пипер, режем на порционни парчета, като за салатата и готвим на грил или обикновен тиган от двете страни до готовност.", "През това време овкусяваме салатата със сол, зехтин, мед и малко лимон.", "Орехите начукваме леко и в сух тиган леко ги позавъртваме, само колкото да си пуснат аромата.", "Салатата Цезар сервираме в чинии, отгоре подреждаме готовото пилешко филе, орехите и поръсваме с пармезан.            "]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000081, 'Салата с печени чушки в буркани', '/static/images/dishes/salata_pecheni.jpg', '["червени чушки - 5 кг", "чесън - 2 глави", "магданоз - 1 връзка", "сол - 50 г", "захар - 100 г", "оцет - 100 мл", "олио - 150 мл"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000081, 'Салата с печени чушки в буркани', 210, '/static/images/dishes/salata_pecheni.jpg', '[{"name": "червени чушки - 5 кг", "imageUrl": "/static/images/dishes/pecheni_chushki.jpg"}, {"name": "чесън - 2 глави", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "магданоз - 1 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "сол - 50 г", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "захар - 100 г", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "оцет - 100 мл", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "олио - 150 мл", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["Чушките се пекат и се слагат в тенджера с похлупак, за да се задушат. Така после по-лесно се почистват от люспите. Почистват се също от дръжки и семки, след което се нарязват на парченца.", "В дълбок съд се приготвя маринатата, като се смеят солта, захарта, олиото и оцета.", "Маринатата се разбърква, слагат се нарязаните чушки, нарязаните на дребно скилидки чесън и пресен магданоз.", "Салатата се разбърква и слага в хладилника за няколко часа, като периодично се разбърква.", "Маринованите чушки се разпределят в буркани заедно маринатата.", "Затварят се плътно и се стерилизират за пет минути от завирането на водата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000032, 'Пукани люти чушлета в сладко-кисел сос', '/static/images/dishes/pukani_chushleta.jpg', '["люти чушки - 2 кг (рибки)", "копър - 1 връзка", "чесън - 1 глава", "захар - 1 кг", "ябълков оцет - 1 литър", "сол - 300 г", "черен пипер - 1 с.л. на зърна"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000032, 'Пукани люти чушлета в сладко-кисел сос', 60, '/static/images/dishes/pukani_chushleta.jpg', '[{"name": "люти чушки - 2 кг (рибки)", "imageUrl": "/static/images/dishes/chushletaribki.jpg"}, {"name": "копър - 1 връзка", "imageUrl": "/static/images/dishes/kopur.jpg"}, {"name": "чесън - 1 глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "захар - 1 кг", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "ябълков оцет - 1 литър", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "сол - 300 г", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "черен пипер - 1 с.л. на зърна", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}]', '["Измийте лютите чушки и отстранете дръжките. Загрейте скарата и ги пукнете за кратко от двете страни.", "Направете сиропа, като сипете оцета и захарта 1:1 в тенджера.", "Когато заври изключете котлона и прибавете сол на вкус, опитвайте и определете по ваш вкус.", "След като се охлади сиропа добавете ситно нарязания копър и чесъна, който е нарязан на едро.", "Чушлетата редете в буркани по ваш избор, но без да се притискат. Колкото повече сладко-кисел сок има, толкова по-добре.", "При отварянето на един буркан първо сокът изчезва, защото е невероятно вкусен. Затваряте с капачки и оставяте без да варите."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000091, 'Сушени домати с подправки', '/static/images/dishes/susheni.jpg', '["домати - 1 кг хубаво узрели сорт Рома или чери", "мащерка - 1 ч.л.", "риган - 1 ч.л.", "сол - 1 - 2 щипки", "зехтин - 20 мл", "чесън - 4 - 5 скилидки", "босилек - 1 ч.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000091, 'Сушени домати с подправки', 380, '/static/images/dishes/susheni.jpg', '[{"name": "домати - 1 кг хубаво узрели сорт Рома или чери", "imageUrl": "/static/images/dishes/cherry_domati.jpg"}, {"name": "мащерка - 1 ч.л.", "imageUrl": "/static/images/dishes/mashrerka.jpg"}, {"name": "риган - 1 ч.л.", "imageUrl": "/static/images/dishes/rigan.jpg"}, {"name": "сол - 1 - 2 щипки", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "зехтин - 20 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "чесън - 4 - 5 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "босилек - 1 ч.л.", "imageUrl": "/static/images/dishes/bosilek.jpg"}]', '["Измийте и подсушете доматите. Разрежете ги на две половинки.", "Наредете ги в тава, застлана с хартия за печене. Поръсете ги с подправките, сол и зехтин.", "Сушете ги в загрята фурна на 100 градуса около 6-7 часа (може и по-малко, зависи колко вода съдържат доматите и каква големина са). Внимавайте да не ги пресушите.", "Оставете да се охладят и приберете сушените домати в хартиени пликове.", "Може да ги мариновате в зехтин с чесън и подправки."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000033, 'Редена салата с домати', '/static/images/dishes/egg_salad.jpg', '["домати - 3 бр. розови", "лук - 1 глава", "сирене - 50 г", "сол", "олио"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000033, 'Редена салата с домати', 5, '/static/images/dishes/egg_salad.jpg', '[{"name": "домати - 3 бр. розови", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "сирене - 50 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}]', '"Доматите се нарязват и се подреждат в подходяща чиния, след това се добавя лукът нарязан на полумесци, поръсва се със сиренето, се подправя със сол и олио и вашата редена салата е готова."');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000046, 'Супа от пилешки дробчета с доматено пюре', '/static/images/dishes/drobcheta.jpg', '["пилешки дробчета - 500 г", "доматено пюре - 50 г", "морков - 1 бр.", "лук - 1 бр.", "ориз - 2 суп. лъжици", "брашно - 1 - 2 чаена лъжички", "кисело мляко - 1/2 чаена чаша", "яйце - 1 бр.", "черен  пипер - 1/2 суп. лъж. + 1 ч.л.", "червен пипер", "магданоз", "масло - 30 г", "джоджен"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000046, 'Супа от пилешки дробчета с доматено пюре', 35, '/static/images/dishes/drobcheta.jpg', '[{"name": "пилешки дробчета - 500 г", "imageUrl": "/static/images/dishes/pileshki_drobcheta.jpg"}, {"name": "доматено пюре - 50 г", "imageUrl": "/static/images/dishes/domaten_sos.jpg"}, {"name": "морков - 1 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "лук - 1 бр.", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "ориз - 2 суп. лъжици", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "брашно - 1 - 2 чаена лъжички", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "кисело мляко - 1/2 чаена чаша", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "яйце - 1 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "черен  пипер - 1/2 суп. лъж. + 1 ч.л.", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "червен пипер", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "масло - 30 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "джоджен", "imageUrl": "/static/images/dishes/djodjen.jpg"}]', '["Измиваме пилешките дробчета, заливаме ги със студена вода и кипваме. Отцеждаме ги от водата и отново ги измиваме.", "В тенджерка задушаваме нарязания лук и почистения и настърган морков в масло и малко водичка. Ръсваме черен пипер, червен пипер на вкус и брашното.", "Наливаме 1 и 1/2 ч.ч. вода и като заври, прибавяме нарязаните дробчета, измития ориз, джодженът, доматеното пюре.", "Варим супата, докато се сварят продуктите. Готовата супа застройваме с разбити яйце и кисело мляко. Преди да свалим от котлона, поръсваме с нарязан ситно магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000032, 'Салата от моркови и яйца', '/static/images/dishes/egg_salad.jpg', '["яйца - 5 бр.", "моркови - 5 бр.", "майонеза - 200 г", "сол", "оцет"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000032, 'Салата от моркови и яйца', 20, '/static/images/dishes/egg_salad.jpg', '[{"name": "яйца - 5 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "моркови - 5 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "майонеза - 200 г", "imageUrl": "/static/images/dishes/maioneza.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/оцет.jpg"}]', '["Салатата е много здравословна и подходяща за всякакво питие. Сложете яйцата да се сварят.", "През това време обелете и настържете морковите в купа.", "Яйцата белете и настържете. Добавете ги при морковите.", "Сложете и майонезата, разбъркайте добре и овкусете яйчената салата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000016, 'Екзотична салата Айсберг', '/static/images/dishes/egzotichna.jpg', '["салата айсберг - 1/2 глава", "авокадо - 1 бр.", "краставици - 1 бр.", "нар - 2 с.л. зърна", "лимонов сок", "зехтин", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000016, 'Екзотична салата Айсберг', 10, '/static/images/dishes/egzotichna.jpg', '[{"name": "салата айсберг - 1/2 глава", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "авокадо - 1 бр.", "imageUrl": "/static/images/dishes/avokado.jpg"}, {"name": "краставици - 1 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "нар - 2 с.л. зърна", "imageUrl": "/static/images/dishes/nar.jpg"}, {"name": "лимонов сок", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}, {"name": "зехтин", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Накъсайте салатата на подходящи парчета.", "Обелете и нарежете авокадото на тънки филийки. Настържете краставицата на тънки колелца.", "Подредете салатата в подходяща чиния и поръсете със зрънца от нар.", "Овкусете зеленчуковата салата преди да поднесете."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000031, 'Салата есенна', '/static/images/dishes/esenna.jpg', '["домати - 5 бр.", "чушки - червени - 2 бр.", "лук - 1 глава", "олио", "сол", "оцет", "магданоз"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000031, 'Салата есенна', 20, '/static/images/dishes/esenna.jpg', '[{"name": "домати - 5 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "чушки - червени - 2 бр.", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/оцет.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}]', '["Чушките се изчистват от дръжките и семето, измиват се и се нарязват надлъжно на четири, а после напречно. ", "Прибавя се нарязания лук, магданозът и нарязаните на по - малки парчета домати. ", "Слага се сол, олио и малко оцет и се разбърква."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000021, 'Салата с риба тон', '/static/images/dishes/fish_salad.jpg', '["домати - 100 г", "краставици - 100 г", "царевица - 50 г", "маслини - 50 г без костилки", "риба тон - консерва", "салати - за канапе"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000021, 'Салата с риба тон', 30, '/static/images/dishes/fish_salad.jpg', '[{"name": "домати - 100 г", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 100 г", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "царевица - 50 г", "imageUrl": "/static/images/dishes/carevica.jpg"}, {"name": "маслини - 50 г без костилки", "imageUrl": "/static/images/dishes/maslini.jpg"}, {"name": "риба тон - консерва", "imageUrl": "/static/images/dishes/ton.jpg"}, {"name": "салати - за канапе", "imageUrl": "/static/images/dishes/aisberg.jpg"}]', '["Нарязват се краставиците и доматите на кубчета.", "На дъното на чинията се поставя салатката, върху нея доматите и краставиците.", "Следват маслините и царевицата симетрично поръсени.", "Отгоре се изсипва консервата риба тон и се украсява с една цяла маслина."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000007, 'Традиционна рибена чорба с девесил', '/static/images/dishes/ribena_chorba.jpg', '["моркови - 1бр.", "зехтин - 4 с.л.", "скумрия - 1 бр.", "лук - 1 глава", "сафрид - 4 - 5 бр.", "чесън - 2 големи скилидки", "чушки - 1 бр. червена", "картофи - 2 средно големи", "дафинов лист - 2 листа", "сол", "целина - суха 2 с.л.", "мащерка - 1 с.л.", "риган - 1 с.л.", "лют червен пипер - 1 ч.л.", "девесил - пресен (на вкус)", "зехтин - 50 мл"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000007, 'Традиционна рибена чорба с девесил', 70, '/static/images/dishes/ribena_chorba.jpg', '[{"name": "моркови - 1бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "зехтин - 4 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "скумрия - 1 бр.", "imageUrl": "/static/images/dishes/skumriq.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "сафрид - 4 - 5 бр.", "imageUrl": "/static/images/dishes/safrid.jpg"}, {"name": "чесън - 2 големи скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "чушки - 1 бр. червена", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "картофи - 2 средно големи", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "дафинов лист - 2 листа", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "целина - суха 2 с.л.", "imageUrl": "/static/images/dishes/celina.jpg"}, {"name": "мащерка - 1 с.л.", "imageUrl": "/static/images/dishes/mashterka.jpg"}, {"name": "риган - 1 с.л.", "imageUrl": "/static/images/dishes/rigan.jpg"}, {"name": "лют червен пипер - 1 ч.л.", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "девесил - пресен (на вкус)", "imageUrl": "/static/images/dishes/devesil.jpg"}, {"name": "зехтин - 50 мл", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["Рибата се сварява предварително в около 2 литра вода, като в бульона при варенето се поставят дафиновите листа и една супена лъжица суха целина.            ", "След като е готов се прецежда и рибата се оставя настрани да се охлади, след което се обезкостява старателно.            ", "В малка касерола се запържват наситненият лук и чесън и се подправят с останалите сухи подправки, като останалата лъжица суха целина се прибавя отделно при прецедения бульон.            ", "В тенджерата се слагат нарязаните зеленчуци и малко наситнен девесил и супата се вари до готовността им. Накрая се прибавя и обезкостената риба и се оставя да къкри на слаб огън още 20 минути.            ", "Готовата рибена чорба се подправя със сол, лимонов сок и допълнително наситнен пресен девесил на вкус, тъй като подправката е доста силна.            ", "Традиционната рибена чорба с девесил е готова."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000036, 'Плодова салата', '/static/images/dishes/plodova.jpg', '["портокали - 1 бр.", "банани - 2 бр.", "нектарини - 1 бр.", "праскови - 1 бр.", "сметана - 1 пакет"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000036, 'Плодова салата', 20, '/static/images/dishes/plodova.jpg', '[{"name": "портокали - 1 бр.", "imageUrl": "/static/images/dishes/portokali.jpg"}, {"name": "банани - 2 бр.", "imageUrl": "/static/images/dishes/banani.jpg"}, {"name": "нектарини - 1 бр.", "imageUrl": "/static/images/dishes/nektarini.jpg"}, {"name": "праскови - 1 бр.", "imageUrl": "/static/images/dishes/praskovi.jpg"}, {"name": "сметана - 1 пакет", "imageUrl": "/static/images/dishes/smetana.jpg"}]', '["Нарязват се на кубчета прасковата и нектарината и се добавят в купичката.", "След това се нарязва на колелца бананите , а портокалът се обелва и се поставя цял или нарязан.", "Като сме сложили всичко в купичката, в отделна купа разбиваме сметаната и добавяме отгоре."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000056, 'Грахова супа с гъби', '/static/images/dishes/grahova.jpg', '["гъби - 100 г.", "грах - 1 консерва", "олио - 3 с.л.", "лук - 1 глава", "моркови - 1 бр.", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000056, 'Грахова супа с гъби', 60, '/static/images/dishes/grahova.jpg', '[{"name": "гъби - 100 г.", "imageUrl": "/static/images/dishes/gubi.jpg"}, {"name": "грах - 1 консерва", "imageUrl": "/static/images/dishes/grah.jpg"}, {"name": "олио - 3 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 1 бр.", "imageUrl": "/static/images/dishes/shunka.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Накълцайте лука и го задушете в мазнина.", "Добавете нарязаните гъби и моркови.", "Когато продуктите омекнат, сложете граха и налейте 5 ч.ч. гореща вода.", "Осолете супата, дръжте я на тих огън още 15 минути и сервирайте."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000051, 'Гръцка салата с червен лук', '/static/images/dishes/gruckas.jpg', '["домати - 100 г", "краставици - 50 г", "червен лук - 1 малка глава", "маруля", "чушки - 1 бр. червена", "сирене фета - 30 г", "зехтин - 50 мл", "риган - на вкус", "лимон - 1/2 бр.", "маслини - 10 бр.", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000051, 'Гръцка салата с червен лук', 5, '/static/images/dishes/gruckas.jpg', '[{"name": "домати - 100 г", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 50 г", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "червен лук - 1 малка глава", "imageUrl": "/static/images/dishes/cherven_luk.jpg"}, {"name": "маруля", "imageUrl": "/static/images/dishes/marulq.jpg"}, {"name": "чушки - 1 бр. червена", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "сирене фета - 30 г", "imageUrl": "/static/images/dishes/sirenef.jpg"}, {"name": "зехтин - 50 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "риган - на вкус", "imageUrl": "/static/images/dishes/rigan.jpg"}, {"name": "лимон - 1/2 бр.", "imageUrl": "/static/images/dishes/limoni.jpg"}, {"name": "маслини - 10 бр.", "imageUrl": "/static/images/dishes/maslini.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измийте доматите добре и ги нарежете на кубчета 1х1 см, обелете краставицата и също я нарежете на кубчета 1х1 см, нарежете чушките и лука на колелца.", "Нарежете марулята на ивици, сиренето фета на кубчета или като мен, което нарязах на триъгълници.", "Подправете зеленчуците със сол, риган и зехтин и разбъркайте всичко.", "След това сервирайте гръцката салатка в чиния и отгоре подредете сиренето фета и маслините.", "Нарежете лимона на пръстени и отстрани украсете салатата от домати и краставици."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000023, 'Традиционна зелена салата', '/static/images/dishes/zelena.jpg', '["зелена салата - 1 бр.", "пресен чесън - 2 - 3 стръка", "краставица - 1 бр.", "репички - 5 - 6 бр.", "олио", "оцет", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000023, 'Традиционна зелена салата', 10, '/static/images/dishes/zelena.jpg', '[{"name": "зелена салата - 1 бр.", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "пресен чесън - 2 - 3 стръка", "imageUrl": "/static/images/dishes/presen_luk.jpg"}, {"name": "краставица - 1 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "репички - 5 - 6 бр.", "imageUrl": "/static/images/dishes/repichki.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измийте салата и я отцедете.", "Нарежете я в голяма купа.", "Обелете чесъна и лука. Тях нарежете на колелца.", "Обелете краставицата. Нарежете и нея заедно с почистените репички.", "Подправете селската зелена салата със сол, оцет и олио.."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000011, 'Зелена салата с яйца, аншоа и маслини', '/static/images/dishes/green-salad-eggs-anchovy-olivs.jpg', '["зелен лук - 1 връзка", "яйца - 8 сварени", "аншоа - 10 - 15 филенца", "маслини - 20 натурални зелени без костилка", "зехтин - 4 с.л. първокачествен", "лимони - сок от 2 бр", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000011, 'Зелена салата с яйца, аншоа и маслини', 30, '/static/images/dishes/green-salad-eggs-anchovy-olivs.jpg', '[{"name": "зелен лук - 1 връзка", "imageUrl": "/static/images/dishes/zelen_luk.jpg"}, {"name": "яйца - 8 сварени", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "аншоа - 10 - 15 филенца", "imageUrl": "/static/images/dishes/anshoa.jpg"}, {"name": "маслини - 20 натурални зелени без костилка", "imageUrl": "/static/images/dishes/maslini.jpg"}, {"name": "зехтин - 4 с.л. първокачествен", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "лимони - сок от 2 бр", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Нарежете зелената салата и лука. Овкусете с част от лимоновия сок и зехтина. Объркайте.", "Обелете сварените яйца и наредете отгоре четвъртинки от тях, разпределете парченца аншоа и маслини.", "Полейте вашата зелена великденска салата с останалия зехтин и лимонов сок.", "Разбира се, тази салата е идеална по всяко време на годината."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000012, 'Зелена салата с авокадо и сок от нар', '/static/images/dishes/zelena-salata-avokado-nar.jpg.jpg', '["маруля - 2 хубави салатки", "яйца - 8 сварени", "зелен чесън - 2 стръка", "авокадо - 2 плода", "зехтин - 4 с.л. първокачествен", "сок от нар - 5 с.л. редуциран /50%/", "калиева сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000012, 'Зелена салата с авокадо и сок от нар', 20, '/static/images/dishes/zelena-salata-avokado-nar.jpg.jpg', '[{"name": "маруля - 2 хубави салатки", "imageUrl": "/static/images/dishes/marulq.jpg"}, {"name": "яйца - 8 сварени", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "зелен чесън - 2 стръка", "imageUrl": "/static/images/dishes/zelen_luk.jpg"}, {"name": "авокадо - 2 плода", "imageUrl": "/static/images/dishes/avokado.jpg"}, {"name": "зехтин - 4 с.л. първокачествен", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сок от нар - 5 с.л. редуциран /50%/", "imageUrl": "/static/images/dishes/nar_sok.jpg"}, {"name": "калиева сол", "imageUrl": "/static/images/dishes/kalieva_sol.jpg"}]', '["Сокът от нар има възкисел вкус. Когато се редуцира, както се продава в някои магазини, е отлична подправка за салатата.", "Нарежете всичко като за салата, авокадото на едро.", "Размесете със зехтина и сока от нар.", "Посолете преди да сервирате, защото солта с повишено съдържание на калий по-бързо извлича водата от зеленчуците."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000025, 'Зелена салата с варени картофи', '/static/images/dishes/green_salad_patato.jpg', '["зелена салата - 1 бр.", "картофи - 4 бр.", "шпек - 100 - 150 г", "кашкавал - 150 г", "кисело мляко - 1/2 кофичка", "майонеза - 100 г"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000025, 'Зелена салата с варени картофи', 40, '/static/images/dishes/green_salad_patato.jpg', '[{"name": "зелена салата - 1 бр.", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "картофи - 4 бр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "шпек - 100 - 150 г", "imageUrl": "/static/images/dishes/salam.jpg"}, {"name": "кашкавал - 150 г", "imageUrl": "/static/images/dishes/kashkaval.jpg"}, {"name": "кисело мляко - 1/2 кофичка", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "майонеза - 100 г", "imageUrl": "/static/images/dishes/maioneza.jpg"}]', '["Картофите се обелват и нарязват на кубчета. Слагат се да се сварят.", "През това време се режат салатата, шпекът и кашкавалът.", "След като се сварят картофите се отцеждат от водата и се прибавят към салатата.", "В отделен съд се смесват майонезата и и киселото мляко. Разбъркват се и се залива зелената салата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000009, 'Гъбена крем супа с масло', '/static/images/dishes/gubena_supa.jpg', '["бульон - 600 мл пилешки", "масло - 1 бучка", "лук - 1 глава", "гъби - 350 грама", "картофи - 1 бр.", "черен пипер", "готварска сметана - 200 мл", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000009, 'Гъбена крем супа с масло', 50, '/static/images/dishes/gubena_supa.jpg', '[{"name": "бульон - 600 мл пилешки", "imageUrl": "/static/images/dishes/bulon.jpg"}, {"name": "масло - 1 бучка", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "гъби - 350 грама", "imageUrl": "/static/images/dishes/gubi.jpg"}, {"name": "картофи - 1 бр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "готварска сметана - 200 мл", "imageUrl": "/static/images/dishes/gotvarska_smetana.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Нарежете на филийки почистените гъби.", "Разтопете маслото в тенджера и задушете гъбите до златист цвят.", "От тях извадете няколко гъбки и ги оставете настрана.", "Наситнете лука и го запържете при гъбите. След това добавете и наситнения картофи, сипете бульона.", "Овкусете с посочените подправки и гответе гъбената супа около 30-тина минути.", "Накрая добавете сметаната и пасирайте до получаване на крем супа. Върнете я на огъня и варете още около 5 минути.", "Сипете в чинии и украсете със заделените гъби, сервирайте кремсупата топла.", "Гъбената крем супа с масло е готова."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000093, 'Стерилизирани гъби', '/static/images/dishes/gabeni.jpg', '["гъби - 1 кг", "оцет - 6 с.л.", "олио - 2 с.л.", "сол - 1 ч.л.", "бахар - 7 зърна", "дафинов лист - 1 бр."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000093, 'Стерилизирани гъби', 120, '/static/images/dishes/gabeni.jpg', '[{"name": "гъби - 1 кг", "imageUrl": "/static/images/dishes/gubi.jpg"}, {"name": "оцет - 6 с.л.", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "олио - 2 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "бахар - 7 зърна", "imageUrl": "/static/images/dishes/bahar.jpg"}, {"name": "дафинов лист - 1 бр.", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}]', '["Почистете и измийте гъбите. Сложете ги в подсолена вряща вода, за да омекнат. Охладете, отцедете и ги нарежете на филийки.", "Вземете два буркана и в тях разпределете равномерно оцета, олиото, бахара, черния пипер и дафиновия лист.", "Отгоре наредете гъбите и ги залейте с водата, в която са врели. Ако е необходимо, добавете още преварена и посолена вода.", "Затворете бурканите и стерилизирайте маринованите гъби 60 минути."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000040, 'Салата с печени гъби', '/static/images/dishes/gubis.jpg', '["печурки - 400 г", "мащерка - 1 с.л.", "чесън - 2 скилидки", "зехтин - 125 мл", "оцет - 2 с.л. Шери", "кресон - 350 г"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000040, 'Салата с печени гъби', 40, '/static/images/dishes/gubis.jpg', '[{"name": "печурки - 400 г", "imageUrl": "/static/images/dishes/pechurki.jpg"}, {"name": "мащерка - 1 с.л.", "imageUrl": "/static/images/dishes/mashterka.jpg"}, {"name": "чесън - 2 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "зехтин - 125 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "оцет - 2 с.л. Шери", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "кресон - 350 г", "imageUrl": "/static/images/dishes/kreson.jpg"}]', '["Предварително загрявате фурната на 220 градуса.", "Смесвате гъбите, листата мащерка, чесъна, оцета и зехтина в съд за печене и подправяте.", "Печете гъбите открити за около 20 минути, или докато омекнат. След това ги оставяте да изстинат.", "Смесвате гъбите и соса с кресона."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000022, 'Пиле с ориз – класическа рецепта', '/static/images/dishes/pileoriz.jpg', '["гъби - 400 г печурки", "чесън - 2 скилидки, скълцан ситно", "черен пипер", "копър - накълцан пресен", "олио - 1 - 2 с.л.", "масло - 2/3 пакетче от 125 г", "сол - на вкус"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000022, 'Пиле с ориз – класическа рецепта', 70, '/static/images/dishes/pileoriz.jpg', '[{"name": "гъби - 400 г печурки", "imageUrl": "/static/images/dishes/gubi.jpg"}, {"name": "чесън - 2 скилидки, скълцан ситно", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "копър - накълцан пресен", "imageUrl": "/static/images/dishes/kopur.jpg"}, {"name": "олио - 1 - 2 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "масло - 2/3 пакетче от 125 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измийте гъбите и отделете гуглите от пънчетата. При нужда ги нарежете на парчета, но е добре да са по-големи.", "Загрейте маслото и олиото в дълбок тиган с капак, сипете гъбите, разбъркайте и оставете да се задушават под капак. От време на време бъркайте.", "След около 10 минути, добавете подправките и чесъна.", "Задушете за още 5-10 минути, махнете от котлона и поръсете с накълцан пресен копър.", "Гъби в масло - разкошно мезе за делник и празник!"]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000055, 'Хайдушка супа', '/static/images/dishes/haidushka.jpg', '["картофи - 200 гр.", "лук - 1/2 глава", "мас - 40 гр.", "кифли - 1 бр.", "колбас - 50 гр.", "бульон - костен", "магданоз", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000055, 'Хайдушка супа', 40, '/static/images/dishes/haidushka.jpg', '[{"name": "картофи - 200 гр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "лук - 1/2 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "мас - 40 гр.", "imageUrl": "/static/images/dishes/mas.jpg"}, {"name": "кифли - 1 бр.", "imageUrl": "/static/images/dishes/kifli.jpg"}, {"name": "колбас - 50 гр.", "imageUrl": "/static/images/dishes/shunka.jpg"}, {"name": "бульон - костен", "imageUrl": "/static/images/dishes/bulon.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Изпържваме в мас ситно нарязания лук и нарязаните на парченца обелените картофи, прибавяме бульона, нарязаната на парченца кифла, посоляваме и варим, докато картофите омекнат.", "Пресоваме супата, прибавяме нарязаните на парченца колбаси и ситно нарязания магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (20000028, 'Български качамак', '/static/images/dishes/kachamak.jpg', '["царевично брашно - 400 г", "масло - 60 г", "прясно мляко - 200 мл.", "сирене - 200 г", "сол - на вкус", "олио - за помазване", "червен пипер - 1 ч.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (20000028, 'Български качамак', 30, '/static/images/dishes/kachamak.jpg', '[{"name": "царевично брашно - 400 г", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "масло - 60 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "прясно мляко - 200 мл.", "imageUrl": "/static/images/dishes/prqsno_mlqko.jpg"}, {"name": "сирене - 200 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "олио - за помазване", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "червен пипер - 1 ч.л.", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}]', '["Кипва се 1.800 л вода с млякото, олиото, солта и се изсипва брашното. Бърка се постоянно.", "Вари се и едновременно с това се разбива с тел в продължение на 10 мин.", "Маслото се сгорещява в тиган и се сипва червеният пипер. Разбърква се.", "Качамакът се разсипва по чинии и се ръси с натрошено сирене. Полива се с пиперосаното масло."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000003, 'Капама с кисело зеле', '/static/images/dishes/kapama.jpg', '["кисело зеле - 1 глава", "лук - 2 глави", "червен пипер - 1 с.л.", "чесън - 2 скилидки", "черен пипер", "сминдух - 20 зърна", "олио - 100 мл", "пилешки бутчета - 4 бр. големи (горен и долен)", "пържоли - 8 бр. от свински врат", "сурова наденица - 3 бр. големи", "пушена сланина - 200 г"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000003, 'Капама с кисело зеле', 90, '/static/images/dishes/kapama.jpg', '[{"name": "кисело зеле - 1 глава", "imageUrl": "/static/images/dishes/kiselo_zele.jpg"}, {"name": "лук - 2 глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "червен пипер - 1 с.л.", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}, {"name": "чесън - 2 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "сминдух - 20 зърна", "imageUrl": "/static/images/dishes/sminduh.jpg"}, {"name": "олио - 100 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "пилешки бутчета - 4 бр. големи (горен и долен)", "imageUrl": "/static/images/dishes/pileshki_but.jpg"}, {"name": "пържоли - 8 бр. от свински врат", "imageUrl": "/static/images/dishes/purjoli.jpg"}, {"name": "сурова наденица - 3 бр. големи", "imageUrl": "/static/images/dishes/nadenica.jpg"}, {"name": "пушена сланина - 200 г", "imageUrl": "/static/images/dishes/slanina.jpg"}]', '["Започнете с месата. Разделете бутчетата на горно и долно, измийте и подсушете.", "Измийте вратните пържоли, наденицата и сланината, подсушете. Оставете месата настрани, докато приготвите зелката.", "Нарежете киселото зеле, лука и чесъна на по-ситно. В тенджера нагрейте олиото, прибавете ситно нарязаните лук и чесън, запържете докато омекнат.            ", "Добавете нарязаното кисело зеле и запържете, докато остане на мазнина. Накрая поръсете с черен и червен пипер, разбъркайте добре и отместете тенджерата от котлона.", "Намажете дъното на голяма тава с капак с олио.", "Подредете пилешкото месо на един ред и поръсете със запърженото кисело зеле. Ред е на вратните пържоли и отново зеле. Отгоре поставете наденичките, предварително им направете разрези и поръсете с останалото зеле.", "Нарежете пушената сланинка на малки парчета и разпределете върху зелето.", "Ако обичате по-пикантна да ви е капамата, поставете и една две-сухи люти чушки.", "Затворете капака на тавата и я поставете в предварително нагрята на 200 градуса фурна.", "Печете на средна решетка на фурната, за около час и половина или до готовност на месата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000008, 'Картофена крем супа с хлебни крутони', '/static/images/dishes/kartofena_supa.jpg', '["картофи - 500 г.", "масло - 50 г.", "лук - 1 глава", "моркови - 1 бр.", "прясно мляко - 100 мл.", "магданоз - 1/2 връзка", "олио", "хляб - 4 - 5 филийки"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000008, 'Картофена крем супа с хлебни крутони', 50, '/static/images/dishes/kartofena_supa.jpg', '[{"name": "картофи - 500 г.", "imageUrl": "/static/images/dishes/bob.jpg"}, {"name": "масло - 50 г.", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 1 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "прясно мляко - 100 мл.", "imageUrl": "/static/images/dishes/milk.jpg"}, {"name": "магданоз - 1/2 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "хляб - 4 - 5 филийки", "imageUrl": "/static/images/dishes/hlqb.jpg"}]', '["Почистете картофите, моркова и лука и ги сварете в подсолена вода.", "Пасирайте зеленчуците с пасатор или кухненски робот до получаването на еднородна смес, оставете водата, в която са врели зеленчуците настрани.", "Добавете маслото, прясното мляко и около 500 мл от водата, в която са врели зеленчуците. Разбъркайте добре до получаване на хомогенна смес и върнете за още 5 - 6 минути.", "За крутоните:", "Нарежете филиите хляб на кубчета. Сложете хартия за печене в тавата и изсипете хлебните кубчета, поръдени със олиото.", "Печете за около 6-7 минути в предварително загрята фурна на 180 градуса до леко зачервяване.", "Готовата крем супа поръсете със магданоз и сервирайте с крутони. Страхотна картофена супа се получи.", "Картофената крем супа с хлебни крутони е готова."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000057, 'Селска кашкавалена супа', '/static/images/dishes/chees.jpg', '["масло - 100 г", "брашно - 100 г", "кашкавал - 200 г настърган", "картофи - 2 бр.", "яйца - 3 бр.", "лимони - 1 бр."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000057, 'Селска кашкавалена супа', 45, '/static/images/dishes/chees.jpg', '[{"name": "масло - 100 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "брашно - 100 г", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "кашкавал - 200 г настърган", "imageUrl": "/static/images/dishes/kashkaval.jpg"}, {"name": "картофи - 2 бр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "яйца - 3 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "лимони - 1 бр.", "imageUrl": "/static/images/dishes/limoni.jpg"}]', '["Загрейте маслото в тенджерка и запържете брашното при непрексъанто разбъркване.", "Разредете запръжката с вода (мляко) и оставете да ври, като добавите предварително обелените и настъргани картофи.", "Овкусете супата със сол и черен пипер. При леко разбъркване добавете и настъргания кашкавал.", "Разбийте яйцата с лимоновия сок и застройте супата. Поднесете с крутони по желание."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000088, 'Обикновен кетчуп с индийско орехче и карамфил', '/static/images/dishes/ketchup.jpg', '["домати - 4 кг", "лук - 8 глави", "захар - 3/4 ч.ч.", "сол - 2 с.л.", "оцет - 1/2 ч.ч.", "кимион - 1 ч.л.", "канела - 1 ч.л.", "индийско орехче - 2 щипки", "черен пипер - 1 ч.л. смлян", "карамфил - 1 ч.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000088, 'Обикновен кетчуп с индийско орехче и карамфил', 110, '/static/images/dishes/ketchup.jpg', '[{"name": "домати - 4 кг", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "лук - 8 глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "захар - 3/4 ч.ч.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "сол - 2 с.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "оцет - 1/2 ч.ч.", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "кимион - 1 ч.л.", "imageUrl": "/static/images/dishes/kimion.jpg"}, {"name": "канела - 1 ч.л.", "imageUrl": "/static/images/dishes/kanela.jpg"}, {"name": "индийско орехче - 2 щипки", "imageUrl": "/static/images/dishes/ind_orehche.jpg"}, {"name": "черен пипер - 1 ч.л. смлян", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "карамфил - 1 ч.л.", "imageUrl": "/static/images/dishes/karamfil.jpg"}]', '["Доматите - добре почистени и измити, обелвам и нарязвам на дребно (може да се смелят с машинка за отделяне на люспите и семките).", "Лука почиствам и измивам, нарязвам на дребно и ги слагам заедно с доматите в тава да врат.", "Добавям оцета и солта. Варя на умерен огън за около час.", "Свалям от огъня и изчаквам да изстине, след което пасирам с пасатора и връщам обратно на огъня.", "След като сместа заври, добавям подправките и разбърквам.", "Варя домашния кетчуп до желаната гъстота, разсипвам добре в измити и добре подсушени буркани или шишета.", "Стерилизирам за 10 минути обикновен кетчуп с индийско орехче и карамфил."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000075, 'Коледни кифли', '/static/images/dishes/koledni-kifli.jpg', '["прясно мляко - 120 мл топло", "яйца - 2 бр.", "захар - 3 с.л.", "сладко", "брашно - 700 г", "мая - 1 пакетче 10 г", "сусам - за поръсване по желание", "сол - 1 ч.л.", "локум", "течен шоколад", "олио - 100 мл"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000075, 'Коледни кифли', 120, '/static/images/dishes/koledni-kifli.jpg', '[{"name": "прясно мляко - 120 мл топло", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "яйца - 2 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "захар - 3 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "сладко", "imageUrl": "/static/images/dishes/kofitur.jpg"}, {"name": "брашно - 700 г", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "мая - 1 пакетче 10 г", "imageUrl": "/static/images/dishes/maq.jpg"}, {"name": "сусам - за поръсване по желание", "imageUrl": "/static/images/dishes/susam.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "локум", "imageUrl": "/static/images/dishes/lokum.jpg"}, {"name": "течен шоколад", "imageUrl": "/static/images/dishes/shokolad.jpg"}, {"name": "олио - 100 мл", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["В дълбок съд изсипваме прясното мляко, топлата вода маята 1 с.л. захар, солта и 2 с.л. брашно.", "Пресяваме си останалото брашното.", "Започваме да си омесваме меко тесто, като добавяме белтъците на яйцата и останалото брашно.", "Жълтъците оставяме за намазване на домашните кифли.", "Готовото получило се тесто оставяме на топло да втаса.", "Втасалото тесто разточваме и нарязване на 8 части.", "На всяка част слагаме плънка по-избор и навиваме като кифлички..", "Вземаме си тавичка постлана с хартия за печене и подреждаме кифлите, като оставяме разстояние между тях.", "Оставяме да втасат, след което мажем с жълтък и поръсваме по желание със сусам.", "Поставяме коледните кифли в печката и печем в предварително загрята фурна до готовност."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000090, 'Кьопоолу по стара рецепта', '/static/images/dishes/kiopoolu2.jpg', '["патладжан - 10 кг", "чушки - 5 кг зелени и червени", "домати - 8 бр. едри", "сол - 1 ч.л.", "олио - 600 мл", "чесън - 1 глава", "захар - 1 с.л.", "магданоз - 1 връзка"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000090, 'Кьопоолу по стара рецепта', 330, '/static/images/dishes/kiopoolu2.jpg', '[{"name": "патладжан - 10 кг", "imageUrl": "/static/images/dishes/patladjani.jpg"}, {"name": "чушки - 5 кг зелени и червени", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "домати - 8 бр. едри", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "олио - 600 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "чесън - 1 глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "захар - 1 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "магданоз - 1 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}]', '["Измийте доматите, патладжана и чушките, подсушете и изпечете, оставете да се задушат.", "Обелете изпечения зеленчук и оставете за 5-6 часа под наклон, да се изцеди сокът им.", "Накълцайте на малки кубчета и съберете зеленчука в голям съд.", "На умерен огън в голяма тава налейте и нагрейте олиото.", "Внимателно прибавете зеленчука при непрекъснато бъркане.", "След като заври, прибавете сол на вкус, захарта, ситно накълцаните скилидки чесън и ситно нарязания магданоз, разбъркайте традиционното кьопоолу много добре.", "Готовото кьопоолу разпределете в сухи буркани.", "Затворете плътно и стерилизирате кьопоолу по стара рецепта за 20 минути след завиране на водата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000022, 'Салата Кипра', '/static/images/dishes/kipra.jpg', '["салата айберг - 1 бр.", "майонеза - 1 пакетче", "царевица - 1 кутия /консервирана/", "яйца - 4 бр. сварени", "сол - на вкус", "червен пипер - по желание"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000022, 'Салата Кипра', 15, '/static/images/dishes/kipra.jpg', '[{"name": "салата айберг - 1 бр.", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "майонеза - 1 пакетче", "imageUrl": "/static/images/dishes/maioneza.jpg"}, {"name": "царевица - 1 кутия /консервирана/", "imageUrl": "/static/images/dishes/carevica.jpg"}, {"name": "яйца - 4 бр. сварени", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "червен пипер - по желание", "imageUrl": "/static/images/dishes/red_paper.jpg"}]', '["Салатата Айберг се измива и надробява на ситно.", "Изсипваме майонезата и омесваме равномерно.", "По средата изсипваме царевицата и украсяваме с яйцата.", "Може яйцата да бъдат поръсени с червен пипер за естетика. Поръсваме сол на вкус."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000086, 'Вкусни краставички в трилитров буркан', '/static/images/dishes/kiseli.jpg', '["корнишони - 2.5кг.", "водка - 30 мл.", "вода - 1.5 л", "сол - 100 г", "копър - щипка", "хрян - 3 бр. лист", "черешови листа - 3 бр", "бахар - 5 зърна", "синап - щипка", "черен пипер - 6 зърна", "винен оцет - 35 мл."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000086, 'Вкусни краставички в трилитров буркан', 30, '/static/images/dishes/kiseli.jpg', '[{"name": "корнишони - 2.5кг.", "imageUrl": "/static/images/dishes/kornishoni.jpg"}, {"name": "водка - 30 мл.", "imageUrl": "/static/images/dishes/vodka.jpg"}, {"name": "вода - 1.5 л", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "сол - 100 г", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "копър - щипка", "imageUrl": "/static/images/dishes/kopur.jpg"}, {"name": "хрян - 3 бр. лист", "imageUrl": "/static/images/dishes/hrqn.jpg"}, {"name": "черешови листа - 3 бр", "imageUrl": "/static/images/dishes/chereshovi.jpg"}, {"name": "бахар - 5 зърна", "imageUrl": "/static/images/dishes/bahar.jpg"}, {"name": "синап - щипка", "imageUrl": "/static/images/dishes/sipan.jpg"}, {"name": "черен пипер - 6 зърна", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "винен оцет - 35 мл.", "imageUrl": "/static/images/dishes/ocet.jpg"}]', '["Първо си измиваме много добре самите краставички.", "В бурканите си поставяме листа от хрян и череша, но може и след като си наредим краставичките, да ги поставим и най-отгоре.", "Заливаме с вряла вода и оставяме да стоят 10 минути.", "Когато отмине това време, изцеждаме водата, защото ще е необходима за приготвяне на марината.", "Към нея добавяме солта и след като заври, наливаме оцета и водката.", "Оставяме отново да заври и изсипваме върху краставичките, поставяме бахара, черния пипери, синапа и завиваме капачките на киселите краставички.", "Обръщаме с капачката надолу вкусните краставички в трилитров буркан."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000084, 'Компот от дюли за укрепване на имунитета', '/static/images/dishes/kompot_duli.jpg', '["дюли - 4 бр. големи", "вода - 2 литра студена", "мед - 8 с.л.", "лимонтузу - 10 г", "индрише - 8 бр. по желание"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000084, 'Компот от дюли за укрепване на имунитета', 45, '/static/images/dishes/kompot_duli.jpg', '[{"name": "дюли - 4 бр. големи", "imageUrl": "/static/images/dishes/duli.jpg"}, {"name": "вода - 2 литра студена", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "мед - 8 с.л.", "imageUrl": "/static/images/dishes/med.jpg"}, {"name": "лимонтузу - 10 г", "imageUrl": "/static/images/dishes/limontozu.jpg"}, {"name": "индрише - 8 бр. по желание", "imageUrl": "/static/images/dishes/indrishe.jpg"}]', '["Дюлите са много полезни за укрепване на имунитета. Този вкусен компот ще ви помогне, когато се чувствате отпаднали.", "В съд си изсипваме два литра студена вода.", "Към нея добавяме лимонтузуто и разбъркваме, докато се разтвори.", "Подготвяме си дюлите като ги измиваме, нарязваме на четвъртинки, премахваме семките и отново нарязваме на кубчета.", "Готовите кубчета поставяме в разтвора, за да не потъмнеят поне 10 минути.", "След това вадим и поставяме в бурканите.", "Във всеки буркан поставяме по 2 супени лъжици мед и по едно индрише по желание.", "Пълним всеки буркан с вода, затваряме с капачките и поставяме за варене.", "Варим 25 минути.", "Съхраняваме на хладно място нашия компот от дюли за укрепване на имунитета."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000089, 'Кьопоолу от тиквички за зимата', '/static/images/dishes/kiopoolu.jpg', '["тиквички - 7 бр.", "олио - 150 мл", "брашно - 7 с.л.", "сол - 1 ч.л.", "оцет - 300 мл", "чесън - 1 глава голяма", "копър - 1 връзка"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000089, 'Кьопоолу от тиквички за зимата', 160, '/static/images/dishes/kiopoolu.jpg', '[{"name": "тиквички - 7 бр.", "imageUrl": "/static/images/dishes/tikvichki.jpg"}, {"name": "олио - 150 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "брашно - 7 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "оцет - 300 мл", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "чесън - 1 глава голяма", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "копър - 1 връзка", "imageUrl": "/static/images/dishes/kopur.jpg"}]', '["Тиквичките се измиват много добре и се почистват от дръжките и краищата.", "Нарязват се на кръгчета. Не се белят.", "В купа се изсипва брашното.", "Прибавя се солта и се разбърква много добре.", "Всяко кръгче се овалва в брашното и се нарежда в намазана в олио тава.", "Тиквичките се нареждат на няколко реда, като всеки ред се полива с олио.", "Пече се в предварително загрята фурна на 200 °C на най-долното ниво на решетката до златисто на най-горния ред.", "След като се изпекат тиквичките, тавата се изважда от фурната.", "Копърът се почиства, измива и нарязва на ситно.", "Чесънът се почиства и нарязва на много ситно.", "В купа се изписва оцетът, копърът и чесънът.", "Разбърква се много добре различното кьопоолу.", "Изпечените тиквички се заливат със сместа и се разбърква, след което се насипват в средни бурканчета за лютеница.", "Бурканите се затварят плътно с капачките.", "Стерилизират се бурканите кьопоолу от тиквички 40 мин след завиране."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000006, 'Супа от коприва с фиде', '/static/images/dishes/kopriva_supa.jpg', '["моркови - 1бр.", "зехтин - 4 с.л.", "коприва - 300г.", "пресен лук - 2 стръка", "магданоз - 1/2 връзка или на вкус", "фиде - 50 г", "яйце - 1", "кисело мляко - 4 с.л.", "лимонов сок - 1 ч.л.", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000006, 'Супа от коприва с фиде', 30, '/static/images/dishes/kopriva_supa.jpg', '[{"name": "моркови - 1бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "зехтин - 4 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "коприва - 300г.", "imageUrl": "/static/images/dishes/kopriva.jpg"}, {"name": "пресен лук - 2 стръка", "imageUrl": "/static/images/dishes/presen_luk.jpg"}, {"name": "магданоз - 1/2 връзка или на вкус", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "фиде - 50 г", "imageUrl": "/static/images/dishes/fide.jpg"}, {"name": "яйце - 1", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "кисело мляко - 4 с.л.", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "лимонов сок - 1 ч.л.", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Копривата се измива добре и се попарва във вряла вода за около 5 минути на котлона. Изважда се и се оставя да се охлади след, което се нарязва на малки късчета.", "Морковът се нарязва на малки кубчета или се настъргва.Пресният лук се нарязва на около 2 см и се запържва леко в олио заедно с моркова. Щом са готови, се се слагат в тенджерата с бульона от попарената коприва и нарязаната охладена коприва и се оставят да се варят 10 минути.", "След като омекне, се слага дребно натрошено фиде. Копривената супа се вари за 5 минути, докато фидето се свари.", "Прави се застройка: Яйцето се разбива в купа с киселото мляко и лимонов сок. След това се добавя магданоз. В края се и добавя една нарязана скилидка чесън.", "Добавя се постепенно около 1/2 л от горещия бульон на пролетната супа, като се бърка непрекъснато. Супата се маха от котлона.", "Оставя се за 5 минути и тогава постепенно застройката се налива в тенджерата, като не се спира да се бърка."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000067, 'Козунак с локум', '/static/images/dishes/kozunak.jpg', '["ЗА МАЯТА", "захар - 1 с.л.", "прясно мляко - 100 мл", "брашно - 2 с.л.", "мая - 42 г прясна", "ЗА ТЕСТОТО", "сол - 2 щипки", "брашно - 1 кг", "яйца - 6 бр.", "прясно мляко - 200 мл", "олио - 200 мл", "захар - 300 г", "лимонов сок - 1 ч.л.лимони - кората от един", "ванилия", "ЗА ПЪЛНЕЖА", "локум - 200 г", "ЗА НАМАЗВАНЕ", "яйца - 1 бр.", "прясно мляко - 2 с.л.", "захар - за поръсване", "бадеми - за поръсване филирани"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000067, 'Козунак с локум', 100, '/static/images/dishes/kozunak.jpg', '[{"name": "ЗА МАЯТА"}, {"name": "захар - 1 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "прясно мляко - 100 мл", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "брашно - 2 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "мая - 42 г прясна", "imageUrl": "/static/images/dishes/maq.jpg"}, {"name": "ЗА ТЕСТОТО"}, {"name": "сол - 2 щипки", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "брашно - 1 кг", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "яйца - 6 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "прясно мляко - 200 мл", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "олио - 200 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "захар - 300 г", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "лимонов сок - 1 ч.л.лимони - кората от един", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}, {"name": "ванилия", "imageUrl": "/static/images/dishes/vaniliq.jpg"}, {"name": "ЗА ПЪЛНЕЖА"}, {"name": "локум - 200 г", "imageUrl": "/static/images/dishes/lokum.jpg"}, {"name": "ЗА НАМАЗВАНЕ"}, {"name": "яйца - 1 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "прясно мляко - 2 с.л.", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "захар - за поръсване", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "бадеми - за поръсване филирани", "imageUrl": "/static/images/dishes/bademip.jpg"}]', '["За да стане козунакът, в стаята, в която го правите, през целия процес трябва да е топло.", "За маята: затоплете леко млякото и в него разтворете маята и захарта, прибавете брашното и разбъркайте до гладка каша, поръсете с малко брашно и оставете да шупне за 10-15 минути на топло.", "В това време пресейте брашното и солта и направете кладенче в него. Затоплете млякото и разтворете в него захарта, ванилията, лимоновата кора и лимоновия сок.", "Разбийте яйцата с половината от олиото.", "След като всички компоненти са готови, може да започнете да месите по следния начин: в кладенчето сипете разбитите с половината олио яйца и вече шупналата мая. Започнете да месите, като постепенно прибавяте сместа от мляко и захар. Месите до пълното усвояване на брашното, после започвате да прибавяте останалото олио, като си намазнявате ръцете и месите до усвояване на олиото от тестото и пак така до изчерпване на мазнината и до получаване на мехурчета по тестото.", "Прехвърлете тестото в намаслена купа и оставете да втаса на топло за поне 2 часа. Премесете вече втасалото тесто и разделете на три равни части за три козунакa. Всеки козунак оформете, като разделите тестото на две равни части. Разстелете всяка част с намаслени ръце на кора.", "Сложете локума и завийте всяка кора на руло и ги сплетете. Застелете с хартия за печене правоъгълна форма и прехвърлете сплетеното тесто. Украсете с парченца локум и оставете да втаса за поне един час на топло.", "По същия начин оформете и останалото тесто. Втасалите козунаци намажете с разбито с прясно мляко и яйце и поръсете със захар и бадеми. Печете в предварително загрята на 180 градуса фурна до готовност.", "Не отваряйте фурната през първите 20 минути от печенето. Когато козунаците се изпекат, ги оставете да изстинат във формите и тогава извадете."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000077, 'Крем карамел с лавандула', '/static/images/dishes/krem.jpg', '["яйца - 6 бр. М размер", "захар - 200 - 250 мл", "прясно мляко - 1 л.", "захар - около 150 г за карамела", "лавандула - около 10 стръкчета сушена"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000077, 'Крем карамел с лавандула', 80, '/static/images/dishes/krem.jpg', '[{"name": "яйца - 6 бр. М размер", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "захар - 200 - 250 мл", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "прясно мляко - 1 л.", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "захар - около 150 г за карамела", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "лавандула - около 10 стръкчета сушена", "imageUrl": "/static/images/dishes/lavandula.jpg"}]', '["Започнете със загряването на млякото - смесете го със захарта и сушените стръкове лавандула. Сложете да заври, разбъркайте на няколко пъти, след което го оставете да се охлади - да не е горещо.", "Важно е да се отбележи, че ако не искате ароматът на лавандула да се усеща силно, може би е добре да намалите количеството ѝ.", "През това време разбийте в подходящ съд яйцата много добре", "Прецедете прясното мляко, за да отстраните билката и евентуалните остатъци от нея.", "Добавете към разбитите яйца на тънка струйка подсладеното и ароматизирано прясно мляко при непрекъснато разбиване.", "Карамелизирайте посоченото количество захар за карамела с 1 с.л. вода - направете светъл карамел, който се разпределя на равни количества в купички за крем карамел. Количеството на карамела може да варира според вкуса ви - повече или по-малко.", "Разпределете млечно-яйчната смес в чашките /по желание отново може да прецедите/. Прехвърлете ги в дълбока тава и долейте студена вода до средата им.", "Сложете крем карамела да се пече на около 130-140 градуса за около 70 минути.", "Готовият крем карамел се оставя да изстине и се обръща в чинийки."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000048, 'Страхотна крем супа от карфиол', '/static/images/dishes/karfiols.jpg', '["карфиол - 1 глава", "лук - 1 глава", "зеленчуков бульон", "зехтин,", "черен пипер", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000048, 'Страхотна крем супа от карфиол', 25, '/static/images/dishes/karfiols.jpg', '[{"name": "карфиол - 1 глава", "imageUrl": "/static/images/dishes/karfiol.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "зеленчуков бульон", "imageUrl": "/static/images/dishes/bulon.jpg"}, {"name": "зехтин,", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Карфиолът се накъсва на розички. Лука белим и режем на ситно.", "Запържваме лука в средно голяма тенжера с малко зехтин, след което добавяме карфиола на розички.", "Доливаме зеленчуковия бульон и варим до готовност и пасираме добре. Овкусяваме и готвим за още няколко минути.", "Поднасяме зеленчуковата супа с вкусни домашни крутончета."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000049, 'Крем супа с моркови и сметана', '/static/images/dishes/morkovis.jpg', '["моркови - 500 г", "лук - 1 глава", "картофи - 2 бр", "готварска сметана - 250 мл,", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000049, 'Крем супа с моркови и сметана', 50, '/static/images/dishes/morkovis.jpg', '[{"name": "моркови - 500 г", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "картофи - 2 бр", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "готварска сметана - 250 мл,", "imageUrl": "/static/images/dishes/gotvarska_smetana.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Морковите и картофите се режат на едро.", "Лукът се разрязва на четири. Всички зеленчуци се слагат в подходяща тенджера и се покриват с вода, около 1 пръст над зеленчуците. Посоляват се и се варят до пълно разваряване.", "След това се пасират на гладко пюре и се добавя сметаната. Морковената крем супа може да се сервира с крутони или препечени филийки хляб."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000034, 'Млечна салата с кускус', '/static/images/dishes/kus.jpg', '["кускус - 1 пакет", "кисело мляко - 400 г", "кисели краставички", "лук - маринован", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000034, 'Млечна салата с кускус', 35, '/static/images/dishes/kus.jpg', '[{"name": "кускус - 1 пакет", "imageUrl": "/static/images/dishes/kus_kus.jpg"}, {"name": "кисело мляко - 400 г", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "кисели краставички", "imageUrl": "/static/images/dishes/kiseli_krastavichki.jpg"}, {"name": "лук - маринован", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Сваряваме кускуса по указание на етикета.", "Отцеждаме добре.", "Нарязваме киселите краставици и лука на ситно. Объркваме заедно всички продукти и посоляваме.", "Оставяме млечната салата в хладилника за 1 час."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000062, 'Лападена супа', '/static/images/dishes/lapads.jpg', '["бульон - 1 кубче гъбен", "лапад - 600 г", "моркови - 1 брой", "картофи - 2 броя", "лук - 1 глава", "прясно мляко - 1 ч.ч.", "розмарин - 2 щипки", "червен пипер - 1/2 ч.л. лют", "черен пипер - на вкус", "нишесте - 1 ч.л. царевично", "кисело мляко - 1 ч.ч.", "яйца - 1 брой", "оцет - 2 с.л.", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000062, 'Лападена супа', 40, '/static/images/dishes/lapads.jpg', '[{"name": "бульон - 1 кубче гъбен", "imageUrl": "/static/images/dishes/bulon.jpg"}, {"name": "лапад - 600 г", "imageUrl": "/static/images/dishes/lapad.jpg"}, {"name": "моркови - 1 брой", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "картофи - 2 броя", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "прясно мляко - 1 ч.ч.", "imageUrl": "/static/images/dishes/prqsno_mlqko.jpg"}, {"name": "розмарин - 2 щипки", "imageUrl": "/static/images/dishes/rozmarin.jpg"}, {"name": "червен пипер - 1/2 ч.л. лют", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "черен пипер - на вкус", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "нишесте - 1 ч.л. царевично", "imageUrl": "/static/images/dishes/nisheste.jpg"}, {"name": "кисело мляко - 1 ч.ч.", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "яйца - 1 брой", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "оцет - 2 с.л.", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измийте и почистете лапада, след което го нарежете на дребно. Нарежете картофите, морковите и лука, прибавете ги към лапада, натрошете отгоре бульона, долейте топла вода и сложете да заври. Оставете да къкри за около 20 минути след завирането.", "Добавете сол, черен и червен пипер, розмарина и прясното мляко. Щом отново заври изсипете и разтвореното в малко вода нишесте и оставете на котлона за още няколко минути.", "Направете застройка от яйцето, киселото мляко и 2 с.л. оцет. Свалете лападената супа от котлона. Темперирайте двете смеси с помощта на черпак и застройте супата. Поднесете супата леко охладена."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000039, 'Салата от леща с чесън', '/static/images/dishes/leshtas.jpg', '["леща - 2 ч.ч. сварена", "чесън - няколко скилидки", "магданоз", "олио", "оцет - 3 с.л.", "черен пипер", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000039, 'Салата от леща с чесън', 5, '/static/images/dishes/leshtas.jpg', '[{"name": "леща - 2 ч.ч. сварена", "imageUrl": "/static/images/dishes/leshta.jpg"}, {"name": "чесън - няколко скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "оцет - 3 с.л.", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Сварената леща се подправя със сол, черен пипер и нарязания магданоз.", "Залива се с олиото и оцета."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000064, 'Яхнията от Леща', '/static/images/dishes/lesta.jpg', '["моркови - 2 бр", "олио - 5 с.л", "червен пипер - 2 ч.л.", "чесън - 4 - 5 скилидки", "лук - 1 глава", "леща", "чубрица", "домати - 1 ч.ч. от консерва", "сухи чушки - 1 чушка червена", "брашно - 1 с.л.", "винен оцет", "магданоз", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000064, 'Яхнията от Леща', 65, '/static/images/dishes/lesta.jpg', '[{"name": "моркови - 2 бр", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "олио - 5 с.л", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "червен пипер - 2 ч.л.", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "чесън - 4 - 5 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "леща", "imageUrl": "/static/images/dishes/leshta.jpg"}, {"name": "чубрица", "imageUrl": "/static/images/dishes/chubrica.jpg"}, {"name": "домати - 1 ч.ч. от консерва", "imageUrl": "/static/images/dishes/domaten_sos.jpg"}, {"name": "сухи чушки - 1 чушка червена", "imageUrl": "/static/images/dishes/suhi_chushki.jpg"}, {"name": "брашно - 1 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "винен оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Почистете лука и морковите и ги нарежете на ситна. Добавете чесъна и целината. Налейте в тенджера 50 мл гореща вода, поставете зеленчуците, овкусете със сол и добавете олиото. Заварете за около 5 минути, докато водата заври.", "Подправете с червен пипер и добавете измитата леща. Долейте около 1 литър гореща вода.", "Сложете на тих огън, похлупете съда и варете 20 минути.", "Прибавете доматите и стритата суха чушка. Варете още 20 минути, докато лещата се свари напълно.", "Подправете с оцет, чубрица и ситно нарязан магданоз.", "Смесете брашното с 2-3 с.л. вода, размийте го и изсипете в яхнията.", "Разбъркайте и оставете да поври още 5 минути, след което може да сервирате."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000095, 'Бърза домашна лютеница', '/static/images/dishes/lutenica.jpg', '["чушки - 3 кг", "домати - 2 кг", "олио - 1/4 ч.ч.", "сол - 3 с.л.", "патладжани - 1 кг", "моркови - 700 г", "лук - 2 - 3 глави", "захар - 2/3 ч.ч.", "оцет - 1/2 ч.ч.", "червено вино - 1/3 ч.ч.", "чесън - 1 глава", "магданоз - 1 - 2 връзки", "черен пипер"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000095, 'Бърза домашна лютеница', 140, '/static/images/dishes/lutenica.jpg', '[{"name": "чушки - 3 кг", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "домати - 2 кг", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "олио - 1/4 ч.ч.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол - 3 с.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "патладжани - 1 кг", "imageUrl": "/static/images/dishes/patladjani.jpg"}, {"name": "моркови - 700 г", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "лук - 2 - 3 глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "захар - 2/3 ч.ч.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "оцет - 1/2 ч.ч.", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "червено вино - 1/3 ч.ч.", "imageUrl": "/static/images/dishes/vino.jpg"}, {"name": "чесън - 1 глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "магданоз - 1 - 2 връзки", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}]', '["Обелете патладжаните, нарежете ги на кубчета, посолете ги и ги оставете да се отцедят.", "Междувременно нарежете останалите продукти на едри парчета, като може да посолите леко доматите и да ги оставите на наклонена повърхност, за да отделят част от излишната вода.", "Сложете всички продукти (без чесъна и магданоза) в голяма и дълбока тава, поръсете със солта, захарта, оцета, виното и олиото и ги разбъркайте добре.", "Покрийте с алуминиево фолио, но не плътно и печете на максимални градуси, докато се изпари водата и зеленчуците останат само на мазнина.", "Отстранете фолиото и добавете нарязания на филийки чесън.", "Печете лютеницата още около 10 минути. Поръсете с магданоз и пасирайте.", "Разпределете в бурканчета, затворете плътно и варете 15-20 минути."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000092, 'Постна лютика', '/static/images/dishes/lutika.jpg', '["домати - около 6 кг", "чушки - около 3 кг, червени и зелени", "магданоз - 1 връзка", "сол - на вкус"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000092, 'Постна лютика', 150, '/static/images/dishes/lutika.jpg', '[{"name": "домати - около 6 кг", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "чушки - около 3 кг, червени и зелени", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "магданоз - 1 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измийте доматите и ги наредете в тава. Печете ги на 200 градуса около 35 минути или докато омекнат. Извадете и оставете да се охладят.", "Измийте и почистете чушките от семките.", "Изпечете, сложете ги в найлонова торбичка да се задушат. Обелете и ги нарежете на парчета.", "Обелете и нарежете доматите и ги добавете към печените чушки. Прибавете сол на вкус и нарязания на ситно магданоз.", "Разбъркайте добре и напълнете в чисти буркани. Затворете и ги варете около 15-20 минути. По желание може да прибавите и печени люти чушлета.", "Продуктите за лютеницата са ориентировъчни. Аз правя на око, добре е доматите да бъдат повече от чушките (2:1)."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000087, 'Люти чушки в оцет', '/static/images/dishes/luti.jpg', '["люти чушки - 1 кг", "оцет - 500 мл", "вода - 500 мл", "сол - 1 с.л.", "чесън - 1 глава", "мед - 1 с.л.", "целина - 1/4 глава", "лук - 4 бр. средно големи глави лук", "синап - 1 пакетче (около 30 г)", "черен пипер - 1 ч.л.", "дафинов лист - 5 - 6 бр."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000087, 'Люти чушки в оцет', 35, '/static/images/dishes/luti.jpg', '[{"name": "люти чушки - 1 кг", "imageUrl": "/static/images/dishes/chushleta.jpg"}, {"name": "оцет - 500 мл", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "вода - 500 мл", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "сол - 1 с.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "чесън - 1 глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "мед - 1 с.л.", "imageUrl": "/static/images/dishes/med.jpg"}, {"name": "целина - 1/4 глава", "imageUrl": "/static/images/dishes/celina.jpg"}, {"name": "лук - 4 бр. средно големи глави лук", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "синап - 1 пакетче (около 30 г)", "imageUrl": "/static/images/dishes/sipan.jpg"}, {"name": "черен пипер - 1 ч.л.", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "дафинов лист - 5 - 6 бр.", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}]', '["Слагаме водата, смесена с оцет, на огъня, добавяме сол, черен пипер, синап, нарязан чесън, мед, ситно нарязана целина, нарязан лук, дафинови листа и оставяме да поври няколко минути, след което изключваме котлона.", "Нарязваме на колелца лютите чушки и ги добавяме към сместа.", "Размесваме добре и слагаме в буркани, като доливаме с течността.", "Оставяме на хладно място. След седмица са готови за консумация."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000013, 'Макарони на фурна – класическа рецепта', '/static/images/dishes/makaroni.jpg', '["захар - 2 с.л.", "вода - 2 литра", "олио - 2 с.л.", "сол - 1 ч.л.", "макарони - 1 пакет (400 г)", "сирене - 200 г", "масло - 30 г", "яйца - 4 бр.", "ванилия - 1 прахче", "прясно мляко - 1 л"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000013, 'Макарони на фурна – класическа рецепта', 80, '/static/images/dishes/makaroni.jpg', '[{"name": "захар - 2 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "вода - 2 литра", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "олио - 2 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "макарони - 1 пакет (400 г)", "imageUrl": "/static/images/dishes/makaroni.jpg"}, {"name": "сирене - 200 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "масло - 30 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "яйца - 4 бр.", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "ванилия - 1 прахче", "imageUrl": "/static/images/dishes/vaniliq.jpg"}, {"name": "прясно мляко - 1 л", "imageUrl": "/static/images/dishes/prqsno_mlqko.jpg"}]', '["В тенджера налейте водата, добавете солта и олиото и оставете за заври. Във врящата вода сипете макароните и варите за 7-8 минути, като периодично разбърквате.", "Прецедете макароните и ги поставете в тавичка, добавете нарязаното на парчета масло и натрошеното сирене, разбъркайте много добре.            ", "За заливката в купа чукнете яйцата и добавете захарта и ванилията, разбъркайте.", "На етапи прибавяйте прясното мляко и бъркате до еднородна смес или докато захарта се разтвори напълно.", "Залейте макароните със сместа и леко и внимателно разбъркайте.            ", "Поставете тавата в предварително нагрята на 220 градуса фурна и печете до получаване на апетитна златисто кафява коричка.", "Оставете макароните на фурна да се охладят напълно, нарежете на порции и сервирайте."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000085, 'Мариновани чушлета', '/static/images/dishes/marinovani_chushleta.jpg', '["чушлета - 1.5 кг", "моркови - 2 бр.", "олио - 3 с.л.", "сол", "оцет"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000085, 'Мариновани чушлета', 35, '/static/images/dishes/marinovani_chushleta.jpg', '[{"name": "чушлета - 1.5 кг", "imageUrl": "/static/images/dishes/chushleta.jpg"}, {"name": "моркови - 2 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "олио - 3 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}]', '["На всяка една чушка си правим дупчици, за да поемат от саламурата маринованите чушлета.", "В тиган изсипваме олио и поставяме на котлона.", "Поставяме чушките и запържваме, а през това време си приготвяме маринатата.", "В дълбок съд си поставяме олиото и към него добавяме оцета и солта.", "Те са в зависимост от вкусовите Ви предпочитания.", "Нарязваме морковите на колелца и тях поставяме в маринатата.", "Готовите изпържени чушки слагаме в купичката с марината и разбъркваме.", "Пълним в бурканчета чушлетата, завиваме капачките и вдигаме за зимата на хладно място."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000072, 'Масленки с конфитюр', '/static/images/dishes/maslenki.jpg', '["свинска мас - ½ ч.ч.", "пудра захар - ½ ч.ч.", "белтък - 1 бр.", "сол - 1 малка щипка", "конфитюр от сини сливи - 13 ч.л. домашен", "брашно - 1 2/3 ч.ч. тип 650"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000072, 'Масленки с конфитюр', 38, '/static/images/dishes/maslenki.jpg', '[{"name": "свинска мас - ½ ч.ч.", "imageUrl": "/static/images/dishes/mas.jpg"}, {"name": "пудра захар - ½ ч.ч.", "imageUrl": "/static/images/dishes/pudra.jpg"}, {"name": "белтък - 1 бр.", "imageUrl": "/static/images/dishes/beltuk.jpg"}, {"name": "сол - 1 малка щипка", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "конфитюр от сини сливи - 13 ч.л. домашен", "imageUrl": "/static/images/dishes/konfitur.jpg"}, {"name": "брашно - 1 2/3 ч.ч. тип 650", "imageUrl": "/static/images/dishes/brashno.jpg"}]', '["Пресяваме пудрата захар с цедка и я слагаме в купа. Вътре добавяме и маста. Маста не я разтопяваме.", "С миксер разбиваме маста и пудрата захар, докато стане на гладък крем.", "После добавяме вътре една малка щипка сол и белтъка и разбиваме отново с миксера. На части прибавяме предварително пресятото брашно и объркваме с ръка. Отива около 1 2/3 ч.ч брашно.", "Трябва да се получи меко маслено тесто, не твърдо. От тестото си прави 26 топчета колкото голям орех.", "Всяко оформяме с ръце, като гледаме да са равни. Нареждаме ги в тавата на фурната, покрита с хартия за печене. В средата на всяко топче почти до дъното правим вдлъбнатина с кръгла капачка на маркер.", "В дупчиците слагаме по ½ ч.л. домашен конфитюр от сини сливи.", "Печем маслените сладки около 8 мин в предварително загрята фурна.", "Подреждаме в чиния и поднасяме с кафе."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000008, 'Домашна майонеза за 30 секунди', '/static/images/dishes/mayonese.jpg', '["олио - 400 мл", "лимонов сок - 1 с.л. прясно изцеден", "яйца - 2 бр. пресни, със средна големина.", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000008, 'Домашна майонеза за 30 секунди', 1, '/static/images/dishes/mayonese.jpg', '[{"name": "олио - 400 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "лимонов сок - 1 с.л. прясно изцеден", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}, {"name": "яйца - 2 бр. пресни, със средна големина.", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Набавете си тесен и висок съд, в който да направите майонезата. Сложете олиото и яйцата вътре, след което добавете подправките", "Включете пасатора на висока степен и го потопете до дъното на съда. Разбивайте в продължение на 8 секунди и бавно го изтегляйте нагоре. Много скоро ще видите, че майонезата е готова.", "Ако не сте доволни от нейната гъстота, можете да я коригирате с малко олио. В случай че предпочитате майонезата да има по-интересен вкус, можете да прибавите към нея и други подправки.            ", "P.S. Много е важно яйцата да бъдат със стайна температура!"]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000053, 'Картофена супа с месо', '/static/images/dishes/mesos.jpg', '["свинско месо - 300 г", "картофи - 1/2 кг.", "магданоз - 1 връзка и половина", "лук - 1 глава кромид", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000053, 'Картофена супа с месо', 65, '/static/images/dishes/mesos.jpg', '[{"name": "свинско месо - 300 г", "imageUrl": "/static/images/dishes/svinsko_meso.jpg"}, {"name": "картофи - 1/2 кг.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "магданоз - 1 връзка и половина", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "лук - 1 глава кромид", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Месото се нарязва на неголеми порционни парчета и се вари за около 40 минути, посолява се.            ", "Картофите се измиват, обелват се и се нарязват на кубчета. Добавят се при месото заедно с лука, а по желание и морков.", "Супата се вари на умерена температура до омекване на продуктите. При сервиране наръсете със ситно нарязания магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000013, 'Пухкави мекици', '/static/images/dishes/mekici.jpg', '["захар - 2 с.л.", "вода - 1 и 1/2 ч.ч. топла", "брашно - 4 ч.ч.", "сол - 1 ч.л.", "бакпулвер - 1/2 ч.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000013, 'Пухкави мекици', 40, '/static/images/dishes/mekici.jpg', '[{"name": "захар - 2 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "вода - 1 и 1/2 ч.ч. топла", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "брашно - 4 ч.ч.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "бакпулвер - 1/2 ч.л.", "imageUrl": "/static/images/dishes/bakpluver.jpg"}]', '["Смесете сухите продукти в голяма купа и прибавете водата, като бъркате до получаване на много гъста каша.", "За да не поемат в последствие при пържене мекичките много мазнина, може да сложите 1 капачка ракия в тестото за мекици.", "Покрийте купата с намаслено фолио и оставете да престои в хладилник до следващата сутрин.", "Тестото трябва да увеличи обема си 3-4 пъти и да е на шупли. Получава се доста лепкаво.", "Поръсете тестото с бакпулвера и омесете отново добре. Оставете да се надигне пак.", "Загрейте повечко мазнина в тиган, намазнете ръце и гребвайте от тестото, като оформяте тънки мекички.", "Изпържете любимите мекици от двете страни.", "Изваждайте готовите върху кухненска хартия.", "Поднесете със солена или сладка гарнитура вече изпържените пухкави мекици."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000082, 'Консервирано свинско месо в буркани', '/static/images/dishes/salata_pecheni.jpg', '["свинска плешка - 1.3 кг", "черен пипер - смлян и на зърна", "дафинов лист", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000082, 'Консервирано свинско месо в буркани', 155, '/static/images/dishes/salata_pecheni.jpg', '[{"name": "свинска плешка - 1.3 кг", "imageUrl": "/static/images/dishes/svinska.jpg"}, {"name": "черен пипер - смлян и на зърна", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "дафинов лист", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Свинската плешка се нарязва на парчета, осолява се и се оставя в хладилника за една нощ. По този нчин се отцежда от излишната вода, дори може да се наложи сутринта и допълнително да се подсуши.", "Месото от прасето може да се запържи леко, колкото да се запечата, но може да се нареди директно в стъклени буркани.", "На дъното на всеки буркан се поставя по един дафинов лист и няколко зрънца черен пипер.", "Парчетата месо се нареждат плътно до горната черта на буркан тип Омния.", "Най-отгоре се поръсва с по половин кафена лъжичка смлян черен пипер. Бурканите с месо се затварят хубаво и се стерилизират за два часа и половина минимум от завирането на водата.", "От това количество месо излизат два буркана тип Омния.", "По този начин имате винаги под-ръка готово свинско месо, което може да добавите към готово ястие или да приготвите нещо набързо с него."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000017, 'Салата Мио Верде', '/static/images/dishes/mioverde.jpg', '["зелени салати - 100 г (зелена, листа червена салата, спанак)", "пушена шунка - 60 г", "круши - 1 бр.", "пармезан - 50 г", "орехи - 60 г белени или бадеми", "зехтин", "горчица - 1 с.л.", "лимонов сок - 2 с.л.", "черен пипер - на вкус", "маруля", "босилек", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000017, 'Салата Мио Верде', 10, '/static/images/dishes/mioverde.jpg', '[{"name": "зелени салати - 100 г (зелена, листа червена салата, спанак)", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "пушена шунка - 60 г", "imageUrl": "/static/images/dishes/shunka.jpg"}, {"name": "круши - 1 бр.", "imageUrl": "/static/images/dishes/krushi.jpg"}, {"name": "пармезан - 50 г", "imageUrl": "/static/images/dishes/parmezan.jpg"}, {"name": "орехи - 60 г белени или бадеми", "imageUrl": "/static/images/dishes/orehi.jpg"}, {"name": "зехтин", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "горчица - 1 с.л.", "imageUrl": "/static/images/dishes/gorchica.jpg"}, {"name": "лимонов сок - 2 с.л.", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}, {"name": "черен пипер - на вкус", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "маруля", "imageUrl": "/static/images/dishes/marulq.jpg"}, {"name": "босилек", "imageUrl": "/static/images/dishes/bosilek.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измийте листата от микс салатите и ги разкъсайте с ръце.", "Добавете белени и нарязани парчета круша. Подправете със смес от зехтин, горчица, лимонов сок, сол и пипер.", "Оформете пирамида от маруля, салатите, филийки круша, шунка и сирене в салатиера.", "Гарнирайте зелената салата с листа от босилек, салата от рукола и орехи."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000009, 'Миш-маш по селски', '/static/images/dishes/mishmash.jpg', '["чушки - 7 - 8 пиперки", "лук - 1 главa", "домати - 4 броя, зрели", "сирене - 150 - 200 г", "черен пипер", "магданоз", "яйца - 8", "масло - 60 г или олио", "сол - на вкус"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000009, 'Миш-маш по селски', 65, '/static/images/dishes/mishmash.jpg', '[{"name": "чушки - 7 - 8 пиперки", "imageUrl": "/static/images/dishes/chuska.jpg"}, {"name": "лук - 1 главa", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "домати - 4 броя, зрели", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "сирене - 150 - 200 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanos.jpg"}, {"name": "яйца - 8", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "масло - 60 г или олио", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Задушете в мазнината нарязания лук. Добавете изпечените, обелени и нарязани пиперки.", "Щом чушките се запържат, добавете и обелените и нарязани на ситно домати. Задушете гозбата, докато течността се изпари напълно.", "Тогава добавете натрошеното сирене, ситно скълцания магданоз и предварително разбитите с малко сол яйца.", "Запържете при непрекъснато разбъркване до стягането на яйцата. Поднесете миш-маша топъл и поръсен с малко черен пипер."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000069, 'Млечна баница', '/static/images/dishes/banitsa.jpg', '["кори - 1/2 пакет", "прясно мляко - 800 мл", "ванилена захар - 1 пакетче", "яйца - 3 - 4 бр.", "маргарин - течен 3/4 ч.ч.", "пудра захар", "захар - 1 ч.ч."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000069, 'Млечна баница', 60, '/static/images/dishes/banitsa.jpg', '[{"name": "кори - 1/2 пакет", "imageUrl": "/static/images/dishes/kori.jpg"}, {"name": "прясно мляко - 800 мл", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "ванилена захар - 1 пакетче", "imageUrl": "/static/images/dishes/vanilena.jpg"}, {"name": "яйца - 3 - 4 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "маргарин - течен 3/4 ч.ч.", "imageUrl": "/static/images/dishes/margarin.jpg"}, {"name": "пудра захар", "imageUrl": "/static/images/dishes/pudra.jpg"}, {"name": "захар - 1 ч.ч.", "imageUrl": "/static/images/dishes/zahar.jpg"}]', '["В намазана с маргарин тава запечете последователно всички кори.", "Разбийте яйцата със захарта и ваниловата захар. Добавете прясното мляко и разбъркайте.", "Начупете корите в дълбока тава и ги залейте със сместа от яйца, захар и прясно мляко. Поръсете отгоре с маргарина. Запечете във фурна, загрята до 220 градуса.", "Сервирайте млечната баница, поръсена с пудра захар."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000068, 'Сладко мляко с ориз', '/static/images/dishes/mlqko_oriz.jpg', '["захар - 1 ч.ч. (може и повече)", "прясно мляко - 1 л", "ванилия - 1 бр", "ориз - 1 ч.ч.", "канела - за поръсване", "вода"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000068, 'Сладко мляко с ориз', 35, '/static/images/dishes/mlqko_oriz.jpg', '[{"name": "захар - 1 ч.ч. (може и повече)", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "прясно мляко - 1 л", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "ванилия - 1 бр", "imageUrl": "/static/images/dishes/vaniliq.jpg"}, {"name": "ориз - 1 ч.ч.", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "канела - за поръсване", "imageUrl": "/static/images/dishes/kanela.jpg"}, {"name": "вода", "imageUrl": "/static/images/dishes/voda.jpg"}]', '["Изчистваме и измиваме ориза. В тенджера сипваме вода колкото да облее ориза и малко над него, така ври около 15 минути при постоянно бъркане.", "Прибавяме по малко мляко и захар като отново бъркаме. Така на няколко пъти добавяме от млякото и захарта.", "Опитваме и ако трябва добавяме още захар или мляко. Готовото мляко с ориз сипваме в купички и поръсваме с канела."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000007, 'Класическо мляко с ориз', '/static/images/dishes/mlqko_oriz.jpg', '["вода - 1 ч.ч.", "прясно мляко - 1 л.", "захар - 1 ч.ч.", "ванилия - 1 прахче (2 г)", "ориз - 1 ч.ч. бисерен"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000007, 'Класическо мляко с ориз', 30, '/static/images/dishes/mlqko_oriz.jpg', '[{"name": "вода - 1 ч.ч.", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "прясно мляко - 1 л.", "imageUrl": "/static/images/dishes/prqsno_mlqko.jpg"}, {"name": "захар - 1 ч.ч.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "ванилия - 1 прахче (2 г)", "imageUrl": "/static/images/dishes/vaniliq.jpg"}, {"name": "ориз - 1 ч.ч. бисерен", "imageUrl": "/static/images/dishes/oriz.jpg"}]', '["Почистете и измийте ориза много добре под течаща вода.", "Поставете го в тенджера заедно с водата и варите докато набъбне (да абсорбира всичката вода). В този момент добавете прясното мляко с щипка сол, бъркате периодично да не загори", "След като отново заври, прибавете захарта и ванилията, варите докато ориза напълно се свари (омекне). Свалете от огъня и разпределете в купички или чашки.", "Докато млякото с ориз се охлади, с лъжичка разбърквайте във всяка купичка внимателно. Това се прави, за да не остане оризът отдолу и сбит, а да е пухкав като крем.", "Сервирайте класическото мляко с ориз напълно охладено и поръсено с канела."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000010, 'Традиционна българска мусака', '/static/images/dishes/musaka.jpg', '["кайма - 500 гр.", "картофи - 1500 гр.", "лук - 100 гр.", "моркови - 50 гр.", "чушки - 70 гр. червени", "магданоз", "домати - на кубчета от консерва 3 с.л.", "олио - 40 мл.", "сол - на вкус", "червен пипер - 2 с.л.", "черен пипер", "кисело мляко - 400 гр.", "яйца - 2 бр.", "брашно - 2 с.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000010, 'Традиционна българска мусака', 65, '/static/images/dishes/musaka.jpg', '[{"name": "кайма - 500 гр.", "imageUrl": "/static/images/dishes/kaima.jpg"}, {"name": "картофи - 1500 гр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "лук - 100 гр.", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 50 гр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "чушки - 70 гр. червени", "imageUrl": "/static/images/dishes/chushka.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanos.jpg"}, {"name": "домати - на кубчета от консерва 3 с.л.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "олио - 40 мл.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "червен пипер - 2 с.л.", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "кисело мляко - 400 гр.", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "яйца - 2 бр.", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "брашно - 2 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}]', '["Нарежете лука, чушката и моркова на ситно. Запържете ги с олиото и каймата в тиган. Бъркайте постоянно, за да се раздроби добре каймата и да стане на трохи. Окусете с червен, черен пипер и сол.", "Нарежете обелените картофи на кубчета и ги сложете в средно голяма тава. Сипете каймата. Разбъркайте добре и налейте гореща вода, колкото да се покрият картофите.", "Гответе в гореща фурна на 200 градуса до омекване на картофите. Добавете доматите и наситнения магданоз. Гответе още около 10 мин.", "Направете заливката като разбиете добре яйцата с млякото и брашното. Сложете щипка сол и полейте равномерно мусаката.", "Печете в средата на фурната само на горен реотан до запичане на заливката на мусаката.", "Сервирайте с лъжица или две кисело мляко."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000015, 'Салата Нардия', '/static/images/dishes/nardiq.jpg', '["салата айсберг - 200 гр.", "маруля - 200 гр.", "зеле - 100 гг.", "домати - 2 бр.", "краставици - 1 бр.", "сос - винегрет"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000015, 'Салата Нардия', 25, '/static/images/dishes/nardiq.jpg', '[{"name": "салата айсберг - 200 гр.", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "маруля - 200 гр.", "imageUrl": "/static/images/dishes/marulq.jpg"}, {"name": "зеле - 100 гг.", "imageUrl": "/static/images/dishes/zele.jpg"}, {"name": "домати - 2 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 1 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "сос - винегрет", "imageUrl": "/static/images/dishes/sos.jpg"}]', '["Почистената салата Айсберг се накъсва на парченца заедно с марулята.", "Доматите и краставиците се нарязват на ситно и се смесват в купа заедно с марулята й Айсберга.", "Добавя се настърганото зеле, леко намачкано с малко сол.", "Всички зеленчуци добре се разбъркват със соса Винегрет.", "Салата Нардия може да поднесете и с малко лук по желание"]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000074, 'Охлювчета с орехи, течен шоколад и канела', '/static/images/dishes/ohluvcheta.jpg', '["прясно мляко - 250 мл топло", "яйца - 2 бр.", "захар - 7 с.л.", "масло - 150 г меко", "брашно - до меко тесто", "суха мая - 1 пакетче (10 г) или 1/2 жива мая (21 г)", "течен шоколад", "орехи", "канела"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000074, 'Охлювчета с орехи, течен шоколад и канела', 60, '/static/images/dishes/ohluvcheta.jpg', '[{"name": "прясно мляко - 250 мл топло", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "яйца - 2 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "захар - 7 с.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "масло - 150 г меко", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "брашно - до меко тесто", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "суха мая - 1 пакетче (10 г) или 1/2 жива мая (21 г)", "imageUrl": "/static/images/dishes/maq.jpg"}, {"name": "течен шоколад", "imageUrl": "/static/images/dishes/shokolad.jpg"}, {"name": "орехи", "imageUrl": "/static/images/dishes/orehi.jpg"}, {"name": "канела", "imageUrl": "/static/images/dishes/kanela.jpg"}]', '["Меси се тесто, което трябва да е меко и пухкаво.", "Веднага се разточва на кора и се маже с течният шоколад, поръсва се с канелата и орехите.", "Навива се на руло и се режат охлювчета.", "Пекат се в загрята фурна на 180 градуса за 20 мин, като малко преди да се извадят се мажат с прясно мляко и се запичат.", "лед изваждане се завиват с кърпа."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000071, 'Ореховки с ябълка', '/static/images/dishes/orehovi.jpg', '["ябълки - 1 кг", "орехи - 250 г", "захар - 500 г", "ванилена захар - 1 бр."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000071, 'Ореховки с ябълка', 50, '/static/images/dishes/orehovi.jpg', '[{"name": "ябълки - 1 кг", "imageUrl": "/static/images/dishes/qbulki.jpg"}, {"name": "орехи - 250 г", "imageUrl": "/static/images/dishes/orehi.jpg"}, {"name": "захар - 500 г", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "ванилена захар - 1 бр.", "imageUrl": "/static/images/dishes/vanilena_zahar.jpg"}]', '["Орехите нарязваме или начукваме на дребно.", "Измиваме и почистваме ябълките. След това ги настъргваме и запържваме със захарта, докато течността от ябълките се изпари.", "След това добавяме орехите и ванилената захар и разбъркваме добре. Изсипваме сместа във форма и оставяме да престои през нощта.", "След това нарязваме на малки ореховки."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000060, 'Оризова супа', '/static/images/dishes/orizena.jpg', '["ориз - 150 ", "праз - 1 стрък", "моркови - 1 брой", "чушки - 1 зелена", "сол - на вкус", "олио - 3 с.л. или 50 г масло", "черен пипер", "чесън - 3 скилидки", "домати - 400 г", "бульон - 1 л зеленчуков", "къри - 1 к.л.", "магданоз - 1/3 връзка", "сметана - 150 г заквасена"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000060, 'Оризова супа', 45, '/static/images/dishes/orizena.jpg', '[{"name": "ориз - 150 ", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "праз - 1 стрък", "imageUrl": "/static/images/dishes/praz.jpg"}, {"name": "моркови - 1 брой", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "чушки - 1 зелена", "imageUrl": "/static/images/dishes/zeleni_chushki.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "олио - 3 с.л. или 50 г масло", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "чесън - 3 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "домати - 400 г", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "бульон - 1 л зеленчуков", "imageUrl": "/static/images/dishes/bulon.jpg"}, {"name": "къри - 1 к.л.", "imageUrl": "/static/images/dishes/kuri.jpg"}, {"name": "магданоз - 1/3 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "сметана - 150 г заквасена", "imageUrl": "/static/images/dishes/smetana.jpg"}]', '["Нарежете всички зеленчуци дребно. Загрейте олио в тенджера и запържете праза, моркова, чушката и чесъна.", "След малко добавете ориза, разбъркайте за кратко и прибавете почистените и ситно нарязани домати.", "Долейте бульона и оставете супата да къкри под капак, докато оризът уври. При нужда може да долеете малко вода. Накрая овкусете с подправките и застройте с разбитата предварително сметана.", "Готовата оризова супа поднесете с малко скълцан пресен магданоз. "]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000050, 'Овчарска салата с пилешко', '/static/images/dishes/ovcharska.jpg', '["домати - 3 бр. средни", "краставици - 1 бр.", "зелени чушки - 1 бр.", "гъби - 150 г мариновани печурки", "пилешки гърди - 100 г", "шунка - 80 г или колбас по избор", "кашкавал - 100 г", "сирене - 70 - 80 г", "магданоз - 1 - 2 стръка, свеж", "пресен лук - 2 стръка", "олио - около 60 г или на вкус", "оцет - по желание", "яйца - 1 бр. твърдо сварено, по желание", "черни маслини - за украса", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000050, 'Овчарска салата с пилешко', 10, '/static/images/dishes/ovcharska.jpg', '[{"name": "домати - 3 бр. средни", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 1 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "зелени чушки - 1 бр.", "imageUrl": "/static/images/dishes/zeleni_chushki.jpg"}, {"name": "гъби - 150 г мариновани печурки", "imageUrl": "/static/images/dishes/pechurki.jpg"}, {"name": "пилешки гърди - 100 г", "imageUrl": "/static/images/dishes/pileshko.jpg"}, {"name": "шунка - 80 г или колбас по избор", "imageUrl": "/static/images/dishes/shunka.jpg"}, {"name": "кашкавал - 100 г", "imageUrl": "/static/images/dishes/kashkaval.jpg"}, {"name": "сирене - 70 - 80 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "магданоз - 1 - 2 стръка, свеж", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "пресен лук - 2 стръка", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "олио - около 60 г или на вкус", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "оцет - по желание", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "яйца - 1 бр. твърдо сварено, по желание", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "черни маслини - за украса", "imageUrl": "/static/images/dishes/maslini.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Пилешкото месо се сварява и оставя да изстине.", "Зеленчуците се почистват, измиват и нарязват - доматите и краставиците на едро, лукът на тънки полумесеци, чушката на кубчета, а гъбите на филийки (ако са малки се оставят цели).", "Магданозът се кълца на ситно или листата се накъсват небрежно.", "Колбасът и част от кашкавала се нарязват на малки кубчета, а пилешкото месо - на по-едри парчета.", "Всичко това се слага в купа и се овкусява със олио, сол и оцет по желание.", "Прехвърля се в дълбока салатиера или индивидуални порции Овчарска салата и отгоре се настъргва сирене, кашкавал и варено яйце.", "Овчарската салата с пиле се украсява с маслинки и се поднася на момента.", "Ако няма да сервирате веднага, приберете в хладилник, без да овкусявате, и направете това непосредствено преди да поднесете."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000061, 'Супа пача', '/static/images/dishes/pacha1.jpg', '["свински крака - 2 бр. ", "лук - 1 глава голяма", "моркови - 1 брой", "целина - ¼ глава и малко листа", "дафинов лист - 1 бр.", "яйца - 1 бр.", "чесън - 0. 5 глава", "хрян - 2 - 3 см корен", "оцет - на вкус"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000061, 'Супа пача', 120, '/static/images/dishes/pacha1.jpg', '[{"name": "свински крака - 2 бр. ", "imageUrl": "/static/images/dishes/svinski_kraka.jpg"}, {"name": "лук - 1 глава голяма", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 1 брой", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "целина - ¼ глава и малко листа", "imageUrl": "/static/images/dishes/celina.jpg"}, {"name": "дафинов лист - 1 бр.", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}, {"name": "яйца - 1 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "чесън - 0. 5 глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "хрян - 2 - 3 см корен", "imageUrl": "/static/images/dishes/hrqn.jpg"}, {"name": "оцет - на вкус", "imageUrl": "/static/images/dishes/ocet.jpg"}]', '["Почистете и измийте свинските крака, поставете ги в тенджера със студена и подсолена вода.", "Поставете ги на котлона да заврят и добавете нарязаните лук, морков, целина - корен и листа, и дафиновия лист. След като се сварят извадете краката и отделете и нарежете месото от кокалчетата.", "Пасирайте чорбата с пасатор и върнете месото в нея, поставете да заври за кратко и отстранете от котлона. Разбийте яйцето, настържете чесъна, хряна при него и налейте оцет на вкус.", "От врялата супа добавяйте по-малко от чорбата към застройката докато се уеднаквят температурите и тогава я сипете към супата.", "Преди сервиране поръсете пачата със смлян черен пипер."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000078, 'Палачинки с мед', '/static/images/dishes/palachinki.jpg', '["яйца - 2 бр.", "брашно - 150 г", "прясно мляко - 300 мл", "ванилия - 1 бр.", "сол - 1 щипка", "масло - 1 ч.л. разтопено краве", "мед", "олио - 1 с.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000078, 'Палачинки с мед', 35, '/static/images/dishes/palachinki.jpg', '[{"name": "яйца - 2 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "брашно - 150 г", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "прясно мляко - 300 мл", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "ванилия - 1 бр.", "imageUrl": "/static/images/dishes/vaniliq.jpg"}, {"name": "сол - 1 щипка", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "масло - 1 ч.л. разтопено краве", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "мед", "imageUrl": "/static/images/dishes/med.jpg"}, {"name": "олио - 1 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["Сложете брашното в подходящ съд и направете кладенче. Разбийте яйцата и ги изсипете в кладенчето. Прибавете половината мляко и разбърквайте интензивно.", "Добавят се солта, ванилията, маслото и олиото. Добавете втората половина мляко, разбъркайте и оставете да престои около 20 минути преди пърженето на палачинките.", "Поднесете палачинките с мед."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (20000020, 'Пататник по смолянски', '/static/images/dishes/patatnik.jpg', '["картофи - 1 кг.", "яйца - 3 бр.", "извара - 3 шепи, натрошена (сирене)", "сол - 1 ч.л. равна на вкус", "джоджен - няколко стръка пресен или сушен", "лук - 1 глава", "масло"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (20000020, 'Пататник по смолянски', 60, '/static/images/dishes/patatnik.jpg', '[{"name": "картофи - 1 кг.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "яйца - 3 бр.", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "извара - 3 шепи, натрошена (сирене)", "imageUrl": "/static/images/dishes/izvara.jpg"}, {"name": "сол - 1 ч.л. равна на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "джоджен - няколко стръка пресен или сушен", "imageUrl": "/static/images/dishes/djodjen.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "масло", "imageUrl": "/static/images/dishes/maslo.jpg"}]', '["Обелваме картофите, измиваме ги и ги настъргваме на ренде. В отделен съд разбъркваме яйцата, изварата, на ситно нарязания лук и джоджен (с пресен е по-вкусно, но може и сушен), и солта.", "Смесваме ги и с настърганите картофи и цялата смес изсипваме в тиган с нагорещена мазнина (масло или др.). Бързо разстиламе по дъното на тигана с незалепващо покритие, така че да е равномерна дебелината навсякъде", "Печем на средно слаб огън и по възможност тиганът да е захлупен и след около 15-20 минути обръщаме внимателно с помощта на капака, като обръщаме рязко и след това с приплъзгване връщаме в тигана.", "Печем още 15-20 минути (вече може без капак). След като е готово яденето, поставяме отгоре една голяма чиния на обратно (с лицето към пататника) и обръщаме, така че долната страна на пататника да се пада отгоре. Поднасяме още топъл.", "Лесен и икономичен пататник по смолянски! Изключително вкусен!"]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000029, 'Картофена салата с червен лук', '/static/images/dishes/kartofena.jpg', '["картофи - 500 г", "червен лук - 1 глава", "олио", "оцет", "копър", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000029, 'Картофена салата с червен лук', 25, '/static/images/dishes/kartofena.jpg', '[{"name": "картофи - 500 г", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "червен лук - 1 глава", "imageUrl": "/static/images/dishes/cherven_luk.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "копър", "imageUrl": "/static/images/dishes/kopur.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Сваряваме картофите, след това ги слагаме в купа и ги мачкаме, като слагаме малко вода.", "Прибавяме солта, олиото и оцета на вкус. Добавяме нарязания на шайби лук и накрая копъра."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000042, 'Картофена супа и сланина', '/static/images/dishes/kartofenas.jpg', '["картофи - 5 - 6 бр.", "сланина - 250 г", "лук - 2 глави", "моркови - 1 брой", "брашно - 2 с.л.", "магданоз - 1/2 връзка", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000042, 'Картофена супа и сланина', 40, '/static/images/dishes/kartofenas.jpg', '[{"name": "картофи - 5 - 6 бр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "сланина - 250 г", "imageUrl": "/static/images/dishes/slanina.jpg"}, {"name": "лук - 2 глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 1 брой", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "брашно - 2 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "магданоз - 1/2 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Нарежете сланината на дребни парченца и я запържете до порозовяване в сух тиган. Извадете я, а в отделената мазнина задушете до омекване ситно нарязания лук. Когато омекне добавете брашното и запържете за кратко.", "Залейте лука и брашното с 6 ч.ч. гореща вода и добавете нарязаните на кубчета картофи, моркови и корени от магданоз. Поръсете със сол на вкус и варете до готовност.", "Преди поднасяне поръсете с малко пържена сланина и ситно нарязан пресен магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000096, 'Пържени патладжани в буркани', '/static/images/dishes/patladjan.jpg', '["магданоз - стръкчета", "лимони", "олио - за пържене", "сол - за поръсване", "патладжани", "сол - на вкус", "дафинов лист - 2 - 3 броя", "бахар - няколко зрънца", "оцет - равна част с олиото", "олио - останало при пърженето на патладжаните, равна част от оцета", "черен пипер - на зърна"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000096, 'Пържени патладжани в буркани', 100, '/static/images/dishes/patladjan.jpg', '[{"name": "магданоз - стръкчета", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "лимони", "imageUrl": "/static/images/dishes/limoni.jpg"}, {"name": "олио - за пържене", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол - за поръсване", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "патладжани", "imageUrl": "/static/images/dishes/patladjani.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "дафинов лист - 2 - 3 броя", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}, {"name": "бахар - няколко зрънца", "imageUrl": "/static/images/dishes/bahar.jpg"}, {"name": "оцет - равна част с олиото", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "олио - останало при пърженето на патладжаните, равна част от оцета", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "черен пипер - на зърна", "imageUrl": "/static/images/dishes/black_paper.jpg"}]', '["Не са посочени количества, защото зависят от желания от вас брой буркани.", "Почистените патладжани нарежете на филийки по дължина и обилно ги посолете, за да се отцедят от горчивината за 1-2 часа.", "След това се попиват и се пържат до готовност от двете страни в сгорещено олио.", "Изпържените патладжани оставете за 1 нощ да престоят. На другия ден ги подреждайте в буркани, като между тях слагате кръгчета лимон и стръкчета нарязан магданоз и чесън.", "Затворете бурканите и ги оставете за 1 денонощие да престоят така. За това време те трябва да са отделили доста олио.", "Останалото от пърженето олио възварете с равна част оцет, сол на вкус и подправките.", "Долейте от маринатата в бурканите и ги затворете. Стерилизирайте за 10 минути."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000047, 'Домашна пилешка супа с картофи', '/static/images/dishes/piles.jpg', '["пилешко месо - 500 г", "фиде - 30 грама", "морков - 1 бр.", "лук - 1 бр.", "картофи - 2 броя", "магданоз - 1/2 връзка"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000047, 'Домашна пилешка супа с картофи', 50, '/static/images/dishes/piles.jpg', '[{"name": "пилешко месо - 500 г", "imageUrl": "/static/images/dishes/pileshko.jpg"}, {"name": "фиде - 30 грама", "imageUrl": "/static/images/dishes/fide.jpg"}, {"name": "морков - 1 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "лук - 1 бр.", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "картофи - 2 броя", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "магданоз - 1/2 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}]', '["В голяма тенджера слагате около 2 литра вода. добавяте пилешкото месо и сол на вкус (около 1/2 чаена лъжица). Колкото повече е пилешкото месо, толкова по-силен ще стане бульонът и толкова по-вкусна ще стане супичката. Ако пък имате баба на село, или пък някаква друга връзка със село е най-добре да си вземете едно домашно пиле, тогава супата ще стане много по-ароматна и със сигурност ще е много по-вкусна!  Имайте предвид обаче, е домашното пиле трябва да ври поне час, за да се свари.По време на варенето на пилето най-вероятно ще се образува пяна, която трябва да отстраните. След като се свари пилето извадете месото от бульона и го прецедете. А кога е сварено пилешкото месо ще разберете като пробвате с виличка. Когато лесно се отделя от костите, значи е готово.", "Нарязвате зеленчуците на малки кубчета. Към бульона добавете първо моркова и лука. След като  поврят  5 минути добавете и нарязаните на дребно картофи, а след още 5 минути добавете и натрошеното фиде и варете до пълно сваряване на фидето и картофите.", "Обезкостете свареното пилешко месо и го накъсайте на дребни парченца, които добавете към супата. Накрая, след като свалите пилешката супа от котлона поръсете със ситно нарязан магданоз. Не е необходимо пилешката супа да ври с магданоза, така той остава свеж, не губи от полезните си вещества, а същевременно овкусява достатъчно добре."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000099, 'Пилешко месо в буркани за зимата', '/static/images/dishes/pile_burkani.jpg', '["пилета - 4 бр. бройлери", "олио - 600 мл", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000099, 'Пилешко месо в буркани за зимата', 240, '/static/images/dishes/pile_burkani.jpg', '[{"name": "пилета - 4 бр. бройлери", "imageUrl": "/static/images/dishes/broileri.jpg"}, {"name": "олио - 600 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Бройлерите се колят, попарват във гореща вода до 60 градуса, да не смъкне кожата им, и се почистват от перушината. Почистват се от вътрешностите и се пърлят с горелка.", "Измиват се добре и нарязваме на едри парчета. Оставя се месото за 1 час във студена вода, докато побелее, после се отцеждат много добре.", "В голяма тенджера се загрява олиото на огнището с дърва и сипваме пилешкото месо на три-четири пъти да се запържи от всички страни. Бърка се с дървена лопатка, а месото се изважда с решетъчна лъжица в съд с капак.", "След всяка порция запържено месо поръсваме със сол и разбъркваме месото.", "Нарежда се в сухи буркани, като се притиска, сипва се по малко от мазнината и затваряме с капачка.", "Бурканите се варят за 1 час и 40 минути след завирането. Оставят се в горещата вода, докато се охладят. На другия ден бурканите се подсушават с кърпа и нареждат на рафта за зимата.", "Излизат точно 10 буркана."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000001, 'Пиле фрикасе', '/static/images/dishes/pile_frikase.jpg', '["яйца - 2 бр.", "кисело мляко - 50 гр", "брашно - около 2 ч.чаши", "пилешко месо - 1 кг", "олио - 100 мл", "черен пипер - 2 г"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000001, 'Пиле фрикасе', 40, '/static/images/dishes/pile_frikase.jpg', '[{"name": "яйца - 2 бр.", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "кисело мляко - 50 гр", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "брашно - около 2 ч.чаши", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "пилешко месо - 1 кг", "imageUrl": "/static/images/dishes/pileshki_but.jpg"}, {"name": "олио - 100 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "черен пипер - 2 г", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}]', '["Пилето се сварява в подсолена вода. Обезкостява се и месото се накъсва на големи парчета.", "В отделна тенджера се загрява олиото. Към него се добавя брашното и се запържва леко. Сваляме от котлона и добавяме горещ бульон от вареното пиле. Бъркаме до получаване на хомогенна смес. Връщаме на котлона и оставяме да поври 5 - 6 мин.", "След като е готово, бавно и внимателно добавяме предварително разбитите жълтъци и кисело мляко. Разбъркваме фрикасето и подправяме с черен пипер.", "Можете да сервирате готовото фрикасе с ориз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000006, 'Пиле с ориз – класическа рецепта', '/static/images/dishes/pileoriz.jpg', '["чушки - 1 бр.", "лук - 2 глави", "червен пипер - 1 с.л.", "ориз - 400 гр", "черен пипер", "моркови - 1 бр.", "олио - 40 мл за пържене", "пиле - 1 бр. около 1.5 кг", "масло - 40 г", "сол - на вкус"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000006, 'Пиле с ориз – класическа рецепта', 70, '/static/images/dishes/pileoriz.jpg', '[{"name": "чушки - 1 бр.", "imageUrl": "/static/images/dishes/chuska.jpg"}, {"name": "лук - 2 глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "червен пипер - 1 с.л.", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}, {"name": "ориз - 400 гр", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "моркови - 1 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "олио - 40 мл за пържене", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "пиле - 1 бр. около 1.5 кг", "imageUrl": "/static/images/dishes/pileshki_but.jpg"}, {"name": "масло - 40 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Нарежете пилето на порции (крилца, бутчета и бяло месо), а гръбнака оставете цял. Измийте много добре и го поставете в тенджера.", "Налейте студена вода и добавете сол (на 1 литър вода 1 ч.л. сол) след като заври премахвайте образувалата се пяна. Нарежете 1 глава лук на ситно и я прибавете към врящото пиле, заедно със зърната черен пипер и целите чушка и морков, варите до полуготовност на месото.", "През това време измийте ориза много добре под течаща вода, като втривате зърната между дланите си и го поставете в купа с леко подсолена студена вода.", "Нарежете другата глава лук на ситно. В тиган нагрейте олиото и запържете за кратко лука, добавете ориза и запържете за около 3-4 минути.", "В тава разстелете запържения ориз равномерно, разпределете нарязаното на порции месо без гръбнака.", "В купата, в която сте мерили ориза, налейте три пъти от чорбата на пилето и я налейте в тавата.", "Посолете на вкус, ако е нужно, нарежете маслото на кубчета равномерно разпределени отгоре и поставете в предварително нагрята на 180 градуса фурна и печете до готовност на ориза, което става за 40 минути.", "Извадете пилето с ориза от фурната и завийте с плътна чиста кърпа, да се задуши за около 10-15 минути.", "У нас като се заговори за пиле с ориз представата е за пиле с чисто бял ориз без зеленчуци и подправки, освен черен пипер. От сварения гръбнак, заедно с чушката, моркова и останалата чорба си правим пилешка супа."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000038, 'Протеинова салата с яйца', '/static/images/dishes/protein.jpg', '["яйца - 2 твърдо сварени", "чесън - няколко скилидки", "сирене", "олио или зехтин", "черен пипер", "червен пипер", "куркума", "хималайска сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000038, 'Протеинова салата с яйца', 20, '/static/images/dishes/protein.jpg', '[{"name": "яйца - 2 твърдо сварени", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "чесън - няколко скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "сирене", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "олио или зехтин", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "червен пипер", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "куркума", "imageUrl": "/static/images/dishes/kurkuma.jpg"}, {"name": "хималайска сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Твърдо сварените яйца се нарязват, чесънът се настъргва или накълцва на ситно и се смесват.", "Яйчената салата се овкусява с подправките и се разбърква хубаво."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000000, 'Пълнени патици на фурна', '/static/images/dishes/palneni_patici.jpg', '["патици - 2 бр. не много големи", "моркови - 2 бр.", "лимони - 1 бр.", "портокали - 1 бр.", "черен пипер - 1 ч.л.", "риган - 1 ч.л.", "мащерка - 1 ч.л.", "сол - 1 ч.л.", "червено вино - 500 мл.", "картофи - 1 кг.", "чесън - 1 глава", "сметана - 2 с.л.", "олио - 200 мл."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000000, 'Пълнени патици на фурна', 155, '/static/images/dishes/palneni_patici.jpg', '[{"name": "патици - 2 бр. не много големи", "imageUrl": "/static/images/dishes/palneni_patici.jpg"}, {"name": "моркови - 2 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "лимони - 1 бр.", "imageUrl": "/static/images/dishes/limoni.jpg"}, {"name": "портокали - 1 бр.", "imageUrl": "/static/images/dishes/portokali.jpg"}, {"name": "черен пипер - 1 ч.л.", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "риган - 1 ч.л.", "imageUrl": "/static/images/dishes/rigan.jpg"}, {"name": "мащерка - 1 ч.л.", "imageUrl": "/static/images/dishes/mashterka.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "червено вино - 500 мл.", "imageUrl": "/static/images/dishes/cherveno_vino.jpg"}, {"name": "картофи - 1 кг.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "чесън - 1 глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "сметана - 2 с.л.", "imageUrl": "/static/images/dishes/smetana.jpg"}, {"name": "олио - 200 мл.", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["Смесваме всички подправки и овкусяваме патиците.", "Оставяме ги да престоят в хладилник за около 24 часа", "Пълним патиците с резени лимон, портокал, парчета моркови и скилидки чесън.", "Печем в тавичка във фурната на 220 градуса.", "Слагаме в тавата гореща вода и ако е необходимо доливаме още вода до пълно изпичане на патиците.", "Когато патиците останат на мазнина и водата се изпари, добавяме картофите и печем 30 мин.", "Приготвяме сос за заливане на печените патици, който включва 1-2 скилидки нарязан чесън, сол и сметаната."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000023, 'Свински вратни пържоли в марината', '/static/images/dishes/purjoli_marinata.jpg', '["червено вино - 200 мл", "прясно мляко - 200 мл", "соев сос - 50 мл.", "черен пипер", "сол - 1/2 ч.л.", "вратни пържоли - 4 бр. по 250 г всяка"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000023, 'Свински вратни пържоли в марината', 40, '/static/images/dishes/purjoli_marinata.jpg', '[{"name": "червено вино - 200 мл", "imageUrl": "/static/images/dishes/vino.jpg"}, {"name": "прясно мляко - 200 мл", "imageUrl": "/static/images/dishes/prqsno_mlqko.jpg"}, {"name": "соев сос - 50 мл.", "imageUrl": "/static/images/dishes/soev_sos.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "сол - 1/2 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "вратни пържоли - 4 бр. по 250 г всяка", "imageUrl": "/static/images/dishes/purjoli.jpg"}]', '["Смесваме подправките. Поръсваме леко дъното на подходящ съд. Слагаме 2 пържоли. Поръсваме с подправки.", "Слагаме другите пържоли и поръсваме с останалите подправки.", "Смесваме виното, млякото и соевия сос, разбъркваме добре и заливаме пържолите, така че да ги покрие.", "Пържолите престояват така 2 часа, след което се изпичат във фурната до приятен загар."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (20000018, 'Автентични Яйца по панагюрски', '/static/images/dishes/qicapn.jpg', '["яйца - 8 бр.", "оцет - 1 - 2 с.л.", "вода - 1.5 л.", "сол", "кисело мляко - 400 мл краве", "краве сирене - 350 г", "чесън - 2 - 3 бр. скилидки", "краве масло - 5 с.л.е", "олио - 3 с.л.", "червен пипер - 1 - 2 ч.л.", "магданоз - 1 връзка, ситно нарязан (по желание)"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (20000018, 'Автентични Яйца по панагюрски', 25, '/static/images/dishes/qicapn.jpg', '[{"name": "яйца - 8 бр.", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "оцет - 1 - 2 с.л.", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "вода - 1.5 л.", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "кисело мляко - 400 мл краве", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "краве сирене - 350 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "чесън - 2 - 3 бр. скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "краве масло - 5 с.л.е", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "олио - 3 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "червен пипер - 1 - 2 ч.л.", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}, {"name": "магданоз - 1 връзка, ситно нарязан (по желание)", "imageUrl": "/static/images/dishes/magdanos.jpg"}]', '["В дълбок съд се налива водата, слага се на котлона, добавя се солта и оцетът и се изчаква да заври. Счупете от ниско едно по едно яйцата внимателно в черпак и след това постепенно ги пуснете във врящата вода. Захлупете съда и ги варете на слаб огън. Ако обичате по-рохки яйца, ги варете около 3 минути, ако ги предпочитате по-твърди, ги варете поне 5 минути.", "Когато се забулят хубаво, с помощта на решетъчна лъжица ги извадете внимателно и леко ги отцедете.", "Подправете киселото мляко с начукания (в хаванче) чесън и малко сол.", "Сиренето натрошете леко и разпределете в четири чинии. По желание може да залеете сиренето с две супени лъжици от водата, в която приготвяте яйцата.", "Във всяка чиния сложете върху сиренето по две яйца. Млякото сипете отстрани.", "Маслото с олиото загрейте на тиган. Ако сложите само масло, то ще прегори.", "Не слагайте червения пипер в тигана!!", "Поръсете с червения пипер яйцата в чиниите и разсипете от загрятото масло във всяка порция.", "По желание се поръсва със ситно нарязан пресен магданоз. Може и копър."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000019, 'Редена салата с домати и краставици', '/static/images/dishes/redena.jpg', '["домати - 1 - 2 бр.", "краставици - 1/2", "зелена салата - половинка", "оцет", "олио", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000019, 'Редена салата с домати и краставици', 5, '/static/images/dishes/redena.jpg', '[{"name": "домати - 1 - 2 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 1/2", "imageUrl": "/static/images/dishes/krastevica.jpg"}, {"name": "зелена салата - половинка", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измиваме и нарязваме доматите. Посоляваме ги, овкусяваме ги с олиото, разбъркваме хубаво и ги слагаме в чиния от едната страна.", "Измиваме и нарязваме краставицата, посоляваме я, овкусяваме я с олио и оцет. Разбъркваме хубаво и изсипваме до доматите, като гледаме да не ги смесим.", "Почистваме, накъсваме салатата листо по-листо, измиваме хубаво на течаща вода и нарязваме.", "Посоляваме я, овкусяваме я с оцет и олио и омачкваме хубаво с ръка, след което я добавяме в свободното място до краставиците и доматите.", "Салатата е много хубава и може да се угоди на всеки вкус. В зелената салата може и да се добави малко зелен лук по желание."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000027, 'Салата от сварено червено цвекло', '/static/images/dishes/cherveno_salad.jpg', '["червено цвекло - 1 глава с листата", "олио", "сол", "оцет"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000027, 'Салата от сварено червено цвекло', 50, '/static/images/dishes/cherveno_salad.jpg', '[{"name": "червено цвекло - 1 глава с листата", "imageUrl": "/static/images/dishes/cveklo.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}]', '["Цвеклото се измива и листата се отделят. В тенджерка с вода се варят цвеклото и листата до готовност. Изваждат се и добре се отцеждат.", "Цвеклото и листата се нарязват на едри парчета в чиния. Овкусяваме с винегрет и сервираме с резени лимон.", "Вкусно и много здравословно. Водата от свареното цвекло може да се пие!"]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000058, 'Родопска супа', '/static/images/dishes/rodopska.jpg', '["масло - 60 г", "царевично брашно - 50 г", "прясно мляко - 1 литър", "орехи - 100 г", "сол - на вкус", "вода - 3 ч.ч"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000058, 'Родопска супа', 35, '/static/images/dishes/rodopska.jpg', '[{"name": "масло - 60 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "царевично брашно - 50 г", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "прясно мляко - 1 литър", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "орехи - 100 г", "imageUrl": "/static/images/dishes/orehi.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "вода - 3 ч.ч", "imageUrl": "/static/images/dishes/voda.jpg"}]', '["Прясното мляко се смесва със водата и се слага на котлона да заври. На тънка струя се прибавя царевичното брашно.", "Супата се осолява и се вари до желаната гъстота. Постоянно трябва да се разбърква.", "След като е готова се добавят маслото и едро счуканите орехи."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000024, 'Руло от тиквички с крема сирене', '/static/images/dishes/rulo_tikvichki.jpg', '["тиквички - 3 бр.", "яйца - 3 бр", "кашкавал - 200 г (настърган )", "черен пипер", "сол - 1/2 ч.л.", "крема сирене - 200 г", "лук - 1 малка глава ( настърган на ренде)", "копър"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000024, 'Руло от тиквички с крема сирене', 30, '/static/images/dishes/rulo_tikvichki.jpg', '[{"name": "тиквички - 3 бр.", "imageUrl": "/static/images/dishes/tikvichki.jpg"}, {"name": "яйца - 3 бр", "imageUrl": "/static/images/dishes/qica.jpg"}, {"name": "кашкавал - 200 г (настърган )", "imageUrl": "/static/images/dishes/kashkaval.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "сол - 1/2 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "крема сирене - 200 г", "imageUrl": "/static/images/dishes/kremasirene.jpg"}, {"name": "лук - 1 малка глава ( настърган на ренде)", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "копър", "imageUrl": "/static/images/dishes/kopur.jpg"}]', '["Измивам и настъргвам тиквичките. Поръсвам със сол и оставям за 20 минути да се изцедят. След това ги слагам в тензух и ги доизтисвам много хубаво.", "Слагам ги в купа и добавям яйцата, 150 г от кашкавала, настъргания лук, ментата, черния пипер, копъра и разбърквам добре.", "В малка тавичка, върху хартия за печене, изсипвам сместа. Пека рулото докато хване златист цвят.", "Оставям настрани да изстине. След това го обръщам обратно, махам хартията и мажа с крема сиренето.", "Поръсвам с останалия кашкавал 50 г, навивам на руло."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (20000029, 'Класически сарми с кисело зеле, кайма и ориз', '/static/images/dishes/sarmi.jpg', '["кисело зеле - 1 зелка", "кайма - 400 кг", "ориз - 1 ч.ч.", "лук - 1 глава", "вода", "сол", "брашно", "червен пипер", "черен пипер", "чубрица", "олио"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (20000029, 'Класически сарми с кисело зеле, кайма и ориз', 160, '/static/images/dishes/sarmi.jpg', '[{"name": "кисело зеле - 1 зелка", "imageUrl": "/static/images/dishes/kzele.jpg"}, {"name": "кайма - 400 кг", "imageUrl": "/static/images/dishes/kaima.jpg"}, {"name": "ориз - 1 ч.ч.", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "вода", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "брашно", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "червен пипер", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "чубрица", "imageUrl": "/static/images/dishes/chubrica.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["Плънката се запържва - лукът, докато стане златист в олио, добавя се каймата и се намачква, готви се още няколко минути, като се добавят малко чубрица и червен пипер.", "Оризът се сварява, докато е готов и се оставя водата да се поеме и да се сгъсти. Плънката и оризът се разбъркват добре.", "Зелето се измива и се разделя на листа.", "Във всеки лист от киселото зеле се слагат по няколко лъжици от плънката в зависимост от големината им и листата се завиват на зелеви сарми.", "Подреждат се в тенджера и се заливат вода. Зелевите сарми се варят за около 50 минути на котлон.", "Когато са готови, се запържва сосчето като в тиган се загрява олио, добавя се брашно и червен пипер, което се изсипва в тенджерата със сармите.", "Оставя се да заври за 5-10 мин."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000030, 'Никулденски шаран', '/static/images/dishes/sharan.jpg', '["шаран - 1 бр., около 1.5 кг", "целина - 5 бр. листа", "лимони - 1 бр.", "лук - 8 средно големи глави", "черен пипер - 1 ч.л.", "домати - 3 бр.", "червен пипер - 2 г", "сол - 1 ч.л.", "орехи - 1 ч.ч.", "чесън - 5 скилидки", "магданоз - 1 връзка", "олио"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000030, 'Никулденски шаран', 90, '/static/images/dishes/sharan.jpg', '[{"name": "шаран - 1 бр., около 1.5 кг", "imageUrl": "/static/images/dishes/jivsharan.jpg"}, {"name": "целина - 5 бр. листа", "imageUrl": "/static/images/dishes/celina.jpg"}, {"name": "лимони - 1 бр.", "imageUrl": "/static/images/dishes/limoni.jpg"}, {"name": "лук - 8 средно големи глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "черен пипер - 1 ч.л.", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "домати - 3 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "червен пипер - 2 г", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "орехи - 1 ч.ч.", "imageUrl": "/static/images/dishes/orehi.jpg"}, {"name": "чесън - 5 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "магданоз - 1 връзка", "imageUrl": "/static/images/dishes/magdanos.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}]', '["Изчистваме и измиваме шарана.", "Нарязваме лука на кръгчета и го задушаваме в олиото и малко вода до омекване.", "Прибавяме нарязания на едро чесън, ситно нарязаните листа от целина, доматите, нарязани на дребно, лимоновия сок, черния и червения пипер и солта.", "Изсипваме зеленчуците в тавичка за печене и отгоре слагаме шарана.", "Печем на 180 градуса, като от време на време заливаме със соса, образуван в тавата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000018, 'Шарена салата Здраве', '/static/images/dishes/sharena.jpg', '["зелена салата - 3 - 4 листа", "краставици - 1/2", "чери домати - 1 шепа", "моркови - 1/2", "домати - 1/2", "зехтин", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000018, 'Шарена салата Здраве', 10, '/static/images/dishes/sharena.jpg', '[{"name": "зелена салата - 3 - 4 листа", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "краставици - 1/2", "imageUrl": "/static/images/dishes/krastevica.jpg"}, {"name": "чери домати - 1 шепа", "imageUrl": "/static/images/dishes/cherry_domati.jpg"}, {"name": "моркови - 1/2", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "домати - 1/2", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "зехтин", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Обелваме домата и го режем на ленти.", "Режем краставицата на ленти и декорираме в чинията по свой вкус. Показала съм моя вариант на декорация.", "Отгоре поръсваме със зехтина и сол, по желания поднасяме и с люта чушка."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000043, 'Чорба от шарен боб с масло по Смолянски', '/static/images/dishes/sharen_bob.jpg', '["боб - 500 г шарен", "олио - 1 - 2 суп. л.", "лук - 2 глави", "моркови - 1 брой", "чушки - 1 бр. зелена", "чушки - 1 бр. червена печена", "сол", "чесън - 1 скилидка", "червен пипер - 1/2 суп. лъж. + 1 ч.л.", "джоджен - щипка", "универсална подправка - 1 суп. л. (пикантина)", "масло - 100 г", "брашно - 1 суп. л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000043, 'Чорба от шарен боб с масло по Смолянски', 115, '/static/images/dishes/sharen_bob.jpg', '[{"name": "боб - 500 г шарен", "imageUrl": "/static/images/dishes/sharenbob.jpg"}, {"name": "олио - 1 - 2 суп. л.", "imageUrl": "/static/images/dishes/slanolioina.jpg"}, {"name": "лук - 2 глави", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 1 брой", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "чушки - 1 бр. зелена", "imageUrl": "/static/images/dishes/zeleni_chushki.jpg"}, {"name": "чушки - 1 бр. червена печена", "imageUrl": "/static/images/dishes/pecheni_chushki.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "чесън - 1 скилидка", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "червен пипер - 1/2 суп. лъж. + 1 ч.л.", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "джоджен - щипка", "imageUrl": "/static/images/dishes/djodjen.jpg"}, {"name": "универсална подправка - 1 суп. л. (пикантина)", "imageUrl": "/static/images/dishes/podpravka.jpg"}, {"name": "масло - 100 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "брашно - 1 суп. л.", "imageUrl": "/static/images/dishes/brashno.jpg"}]', '["Бобът се накисва предварително във вода за няколко часа. Отцежда се и се поставя в тенджера с вода, колкото да го покрие.", "Слага се на котлона докато заври, да поври около 5 минути, и първата вода се изхвърля. Налива се топла вода и отново се поставя на огъня.", "Кромидът и морковът се нарязват на ситни кубчета и прибавят при боба. Посолява се леко, слагаме малко олио, 1/2 суп. л. червен пипер, скилидката чесън и се оставя да къкри около час и половина на умерено силна температура.", "Когато бобът се свари се прибавят наситнените зелена и червена чушки, джодженът, пикантината. Ако е нужно се овкусява с още малко сол.", "В тиган запържете леко кравето масло с брашното и малко червен пипер. Изсипете запръжката в горещия боб и оставете гозбата да къкри още 5-7 минути.", "В Смолянско шареният фасул се приготвя по този специфичен начин. Тайната е в запръжката с кравето масло.", "При сервиране, ако желаете, поръсете боб чорбата с накълцани пресни зелени подправки (магданоз, джоджен) и прибавете люто чушле като добавка."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000000, 'Класическа шкембе чорба с прясно мляко', '/static/images/dishes/shkembe_chorba.jpg', '["шкембе - 720 г. телешко, почистено и сварено + на око и по желание почистени говежди или телешки дебели черва или шердени от всички видове", "прясно мляко - 500 мл", "мазнина - 30 г", "чесън - 25 г", "оцет - 40 мл", "червен пипер - 5 г", "черен пипер - 4 - 5 зрънца", "бахар - 1 - 2 зрънца", "дафинов лист - 1 брой", "магданозени корени - 100 г ароматни", "лук - 50 г кромид", "праз - 50 г кромид"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000000, 'Класическа шкембе чорба с прясно мляко', 150, '/static/images/dishes/shkembe_chorba.jpg', '[{"name": "шкембе - 720 г. телешко, почистено и сварено + на око и по желание почистени говежди или телешки дебели черва или шердени от всички видове", "imageUrl": "/static/images/dishes/shkembe.jpg"}, {"name": "прясно мляко - 500 мл", "imageUrl": "/static/images/dishes/milk.jpg"}, {"name": "мазнина - 30 г", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "чесън - 25 г", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "оцет - 40 мл", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "червен пипер - 5 г", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "черен пипер - 4 - 5 зрънца", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "бахар - 1 - 2 зрънца", "imageUrl": "/static/images/dishes/bahar.jpg"}, {"name": "дафинов лист - 1 брой", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}, {"name": "магданозени корени - 100 г ароматни", "imageUrl": "/static/images/dishes/magdanoz_koren.jpg"}, {"name": "лук - 50 г кромид", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "праз - 50 г кромид", "imageUrl": "/static/images/dishes/praz.jpg"}]', '["Шкембетата, добре почистени, трябва да се сварят 5-6 часа на слаб котлон в закрит съд. ", "За да се получи плътен и вкусен бульон заедно с шкембетата е желателно да се варят говежди или телешки крака заедно с парче говежда и бъбречна лой.", "Пяната се обира с решетъчна лъжица. Съотношението между шкембето и водата е 1:4.", "Шкембетата се изваждат, към бульона се долива малко вода и прясно мляко и се вари още 10-15 минути. Към края на варенето се добавят морков и целина, магданозени корени, обелен лук, листа от целина, праз лук, и кромид.", "Бульонът се подправя с листа черен пипер, бахар, дафинов лист, малко чесън и сол.", "Свареното шкембе веднага се потапя в студена вода и добре се отцежда до изсушаване.", "Събраната мазнина от бульона се размесва с червен пипер и мазнина, размесва се и се влива в бульона, който ври с мазнината 10-15 минути.", "Бульонът се прецежда и мазнината се обира .Част от свареното шкембе се пуща в горещия бульон и се кипва. При поднасяне вареното шкембе се изважда от съда, накълцва се и се слага в купичка.", "Залива се с бульона, прибавя се мазнина и се подправя с пресован чесън, размесен с оцет и сол. По желание шкембе чорбата се поднася със счукани сушени червени люти чушки.", "Класическата шкембе чорба с прясно мляко е готова."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000052, 'Гръцка салата с червен лук', '/static/images/dishes/shopskas.jpg', '["домати - 2 бр.", "краставици - 2 бр.", "магданоз - 1 връзка и половина", "лук - 1 връзка пресен или 1 глава кромид", "чушки - 2 броя зелени", "маслини - 150 г", "олио", "сирене - 100 грама", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000052, 'Гръцка салата с червен лук', 15, '/static/images/dishes/shopskas.jpg', '[{"name": "домати - 2 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 2 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "магданоз - 1 връзка и половина", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "лук - 1 връзка пресен или 1 глава кромид", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "чушки - 2 броя зелени", "imageUrl": "/static/images/dishes/zeleni_chushki.jpg"}, {"name": "маслини - 150 г", "imageUrl": "/static/images/dishes/maslini.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сирене - 100 грама", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Обелвате си краставиците ала зебра - т.е. белите ги през ред и ги нарязвате на полумесеци.", "Доматите се нарязват на кубчета, а чушките - на ивици, лукът - на ситно. Добавяте маслините и овкусявате със солта и олиото.", "Взимате чиния за сервиране и кръстосвате обелената на по-дебело краставица.", "В така получения пръстен слагате от салатата и поръсвате с наситнения магданоз. Отгоре настъргвате сирене и си правите розичка от кората на домата, която слагате върху салатата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000097, 'Маринована сланина в буркани Омния', '/static/images/dishes/slaninab.jpg', '["сланина - по възможност до 2.5 - 3 см. дебелина от не голямо прасенце /може и с кожата/ независимо от количеството", "вода - 3 л", "морска сол - 1 кг", "дафинов лист - 2 - 3 броя", "черен пипер - на зърна"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000097, 'Маринована сланина в буркани Омния', 80, '/static/images/dishes/slaninab.jpg', '[{"name": "сланина - по възможност до 2.5 - 3 см. дебелина от не голямо прасенце /може и с кожата/ независимо от количеството", "imageUrl": "/static/images/dishes/slanina.jpg"}, {"name": "вода - 3 л", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "морска сол - 1 кг", "imageUrl": "/static/images/dishes/morska_sol.jpg"}, {"name": "дафинов лист - 2 - 3 броя", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}, {"name": "черен пипер - на зърна", "imageUrl": "/static/images/dishes/black_paper.jpg"}]', '["Сланината се нарязва на по 4-6 см широчина и дължина по височината на буркана, но не по-вече от 11 см. Залива се с вода да се изкисне за 2-3 дена.", "За по-удобно може да се използва и кошничка от фритюрник, като нарязаните предварително парчета сланина се поставят в нея.", "Маринатата се приготвя, след като заври и солта се разтвори в подходящ съд за попарване на сланината.", "Кошничката с наредената в нея сланина се потапя във врящата марината при което тя изстива, но в последствие отново завира.", "След повторното ѝ завиране се изважда след 1 минута, но не и преди това. Повтаря се, докато се попари цялото количество сланина.", "Сланината се нарежда плътно в бурканите като при това, според предпочитанията, може да си сложете в буркан по дафинов листо, 1 скилидка чесън, 2-3 зърна черен пипер.", "Маринатата се налива в бурканите едва след като изстине. Бурканите се запечатват.", "След около 20-25 дни е готова за консумация."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000030, 'Домашна салата Снежанка', '/static/images/dishes/snejanka.jpg', '["кисели краставички - 1 буркан", "цедено мляко - 500 г", "чесън - две скилидки", "копър", "орехи"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000030, 'Домашна салата Снежанка', 20, '/static/images/dishes/snejanka.jpg', '[{"name": "кисели краставички - 1 буркан", "imageUrl": "/static/images/dishes/kiseli_krastavichki.jpg"}, {"name": "цедено мляко - 500 г", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "чесън - две скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "копър", "imageUrl": "/static/images/dishes/kopur.jpg"}, {"name": "орехи", "imageUrl": "/static/images/dishes/orehi.jpg"}]', '["Нарязвате краставичките, чесъна и копъра ситно.", "Изсипвате в купа цеденото мляко.", "Разбърквате хубаво.", "Добавяте орехите според вкуса ви.", "Ако сместа е твърде гъста, може да добавите една супена лъжица кисело мляко.", "Може да украсите млечната салата по желание."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000100, 'Бял сос с кисело мляко', '/static/images/dishes/sosbql.jpg', '["кисело мляко - 1 кофичка", "олио - 1 с.л.", "масло - 2 с.л.", "брашно - 2 с.л.", "жълтък - 1 бр.", "вода - 1 к.ч. или сос от печените чушки", "сол - на вкус", "черен пипер", "лимонов сок - 2 с.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000100, 'Бял сос с кисело мляко', 40, '/static/images/dishes/sosbql.jpg', '[{"name": "кисело мляко - 1 кофичка", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "олио - 1 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "масло - 2 с.л.", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "брашно - 2 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "жълтък - 1 бр.", "imageUrl": "/static/images/dishes/jultuci.jpg"}, {"name": "вода - 1 к.ч. или сос от печените чушки", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "лимонов сок - 2 с.л.", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}]', '["В тенджерка се загрява маслото и олиото. Брашното се добавя и се запържва на слаб огън, да не прегори, като се бърка непрекъснато.", "След като промени цвета си, се разрежда с охладения сос от чушки или водата. Посолява се на вкус, разбърква се с телена бъркалка до хомогенност и се оставя за минута на котлона да клокне.", "Сваля се от огъня тенджерката, добавя се киселото мляко с разбит в него жълтък и отново се слага на котлона само за 1 минута, добавяте и лимонов сок и сосът за зеленчуци е готов.", "Може да се сервира с пълнени чушки, пържени картофени кюфтета или сарми!"]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000005, 'Супа топчета', '/static/images/dishes/topcheta.jpg', '["кайма - 400 г.", "моркови - 2 бр.", "лук - 1 глава", "доматен сок - 1 ч.ч.", "яйца - 2 бр.", "кисело мляко - 4 с.л.", "брашно - 2 с.л.", "червен пипер - 1 с.л.", "черен пипер", "чубрица", "магданоз", "олио", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000005, 'Супа топчета', 50, '/static/images/dishes/topcheta.jpg', '[{"name": "кайма - 400 г.", "imageUrl": "/static/images/dishes/kaima.jpg"}, {"name": "моркови - 2 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "доматен сок - 1 ч.ч.", "imageUrl": "/static/images/dishes/domaten_sos.jpg"}, {"name": "яйца - 2 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "кисело мляко - 4 с.л.", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "брашно - 2 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "червен пипер - 1 с.л.", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "чубрица", "imageUrl": "/static/images/dishes/chubrica.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Омесете каймата със подправките, а може да ползвате и готова овкусена от пакетчета.", "Оформете малки топчета, нарежете лука на ситно, а морковите - на кръгчета.            ", "Сложете олио в тенджера и задушете лука с морковите. Сипете червения пипер, доматения сок и водата, след като заври супата, пуснете топчетата.", "Когато се свари супата, поръсете нарязания магданоз и изключете котлона.            ", "Направете застройката, като разбиете яйцата с киселото мляко и брашното.            ", "От горещата супа топчета сипвайте по малко към яйцата, докато температурите се уеднаквят, след което сипете към супата."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000044, 'Пролетна картофена супа със спанак и киселец', '/static/images/dishes/spanaks.jpg', '["картофи - 400 г пресни", "спанак - 1 шепа", "киселец - 1 шепа", "заготовка за супи - 1 бурканче от пюре (морков, чушка, домат)", "фиде - 4 с.л.", "растителна мазнина - 3 ч.л.", "сол", "жълтъци - 1 бр.", "черен  пипер - 1/2 суп. лъж. + 1 ч.л.", "кисело мляко - 3 с.л., с връх", "индийско орехче - 1 - 2 щипки, прясно настъргано            ", "масло - 40 г", "орехи - запечени"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000044, 'Пролетна картофена супа със спанак и киселец', 40, '/static/images/dishes/spanaks.jpg', '[{"name": "картофи - 400 г пресни", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "спанак - 1 шепа", "imageUrl": "/static/images/dishes/spanak.jpg"}, {"name": "киселец - 1 шепа", "imageUrl": "/static/images/dishes/kiselec.jpg"}, {"name": "заготовка за супи - 1 бурканче от пюре (морков, чушка, домат)", "imageUrl": "/static/images/dishes/zagotovka.jpg"}, {"name": "фиде - 4 с.л.", "imageUrl": "/static/images/dishes/fide.jpg"}, {"name": "растителна мазнина - 3 ч.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "жълтъци - 1 бр.", "imageUrl": "/static/images/dishes/jultuci.jpg"}, {"name": "черен  пипер - 1/2 суп. лъж. + 1 ч.л.", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "кисело мляко - 3 с.л., с връх", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "индийско орехче - 1 - 2 щипки, прясно настъргано            ", "imageUrl": "/static/images/dishes/ind_orehche.jpg"}, {"name": "масло - 40 г", "imageUrl": "/static/images/dishes/maslo.jpg"}, {"name": "орехи - запечени", "imageUrl": "/static/images/dishes/orehi.jpg"}]', '["Пресните картофки се измиват и обелват. Нарязваме ги на кубчета и ги бланшираме в леко подсолена вода.", "Растителната мазнина се загрява и нея се задушават картофените кубчета.", "Прибавяме съдържанието на бурканчето със заготовка за супи, фидето и гореща вода от бланшираните картофи.", "Измитите и почистени от дръжки зелении нарязваме на ситно.            ", "Сотираме ги за кратко в маслото, след което ги прибавяме към къкрещата супа.", "Варенето продължава още 3-5 минути. Овкусяваме със сол, черен пипер и прясно настъргано индийско орехче.", "В купа разбиваме жълтъка с киселото мляко. Прибавяме към сместа и нарязания магданоз.", "Темперираме застройката с черпак от горещия бульон.", "Поднасяме картофената супа топла, като към нея можете да добавите малка бучица масло.", "Аранжираме със запечени и осолени орехови ядки."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000014, 'Пролетна салата с гръцки маслини', '/static/images/dishes/spring.jpg', '["репички - 4 - 5 бр.", "маслини - 5 - 6 бр. гръцки", "зехтин", "ябълков оцет", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000014, 'Пролетна салата с гръцки маслини', 10, '/static/images/dishes/spring.jpg', '[{"name": "репички - 4 - 5 бр.", "imageUrl": "/static/images/dishes/repichki.jpg"}, {"name": "маслини - 5 - 6 бр. гръцки", "imageUrl": "/static/images/dishes/maslini.jpg"}, {"name": "зехтин", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "ябълков оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Свежата салата се почиства и измива добре листо по листо. Нарязва се ситно в купа, добавят се измити и нарязани на полумесеци репички.", "Поръсваме със сол на вкус, зехтин и ябълков оцет.", "Разбърква се добре, сипваме в чиния.", "Две от червените репички с различен размер, една по-малка и една по-голяма се нарязват на тънки кръгчета и нареждаме върху зелената салата като цвете.", "Гарнираме с гръцки едри маслини, поръсваме с още малко зехтин и сервираме."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000019, 'Студена пача', '/static/images/dishes/studenapacha.jpg', '["свинско месо - 1 кг.", "свински крачета - 3 броя", "чесън", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000019, 'Студена пача', 140, '/static/images/dishes/studenapacha.jpg', '[{"name": "свинско месо - 1 кг.", "imageUrl": "/static/images/dishes/svinsko.jpg"}, {"name": "свински крачета - 3 броя", "imageUrl": "/static/images/dishes/kracheta.jpg"}, {"name": "чесън", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Сваряват се месото и крачетата в тенджера под налягане, за да станат по бързо.", "След това се начупва месото, слага се в бульона, овкусява се с чесън и сол и пачата се оставя да изстине."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000101, 'Сушено месо от свинско бон филе или бут', '/static/images/dishes/susheno_meso.jpg', '["чубрица - около 200 г", "свинско бон филе - 1 кг или месо от бут", "захар - 1 ч.л.", "червен пипер - около 100 г", "черен пипер"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000101, 'Сушено месо от свинско бон филе или бут', 25, '/static/images/dishes/susheno_meso.jpg', '[{"name": "чубрица - около 200 г", "imageUrl": "/static/images/dishes/chubrica.jpg"}, {"name": "свинско бон филе - 1 кг или месо от бут", "imageUrl": "/static/images/dishes/svinska_meso.jpg"}, {"name": "захар - 1 ч.л.", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "червен пипер - около 100 г", "imageUrl": "/static/images/dishes/red_paper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}]', '["В тава се прави легло от сол, слага се върху него свинското бонфиле и отгоре се покрива пак със сол.", "Оставя се за 24 часа на студено, за да може да излезе водата от него. След тези 24 часа, месото се измива под течаща вода, за да падне солта и се подсушава с кухненска хартия.", "Нарязва се, всеки решава колко дебели да са парчетата месо. Прави се дупка от едната страна и се прокарва канап, за да бъде по-лесно закачането.", "В една купа се слагат чубрицата, захарта, червеният пипер и черният пипер. Овалват се добре парчетата месо и се закачват на студено и проветриво място, в никакъв случай не се слагат в хладилник, защото месото се овлажнява.", "След 7-9 дни сушеницата трябва да е готова.", "Може да се съхранява във фризер, така и летните месеци ще можете да си похапвате."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000023, 'Салата от свежи зеленчуци', '/static/images/dishes/sveja.jpg', '["салата - 100 - 150 г млечна, готова.", "домати - 1 бр.", "краставици - 1/2 бр. прясна", "репички - 5 - 6 бр.", "магданоз", "зехтин", "оцет", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000023, 'Салата от свежи зеленчуци', 10, '/static/images/dishes/sveja.jpg', '[{"name": "салата - 100 - 150 г млечна, готова.", "imageUrl": "/static/images/dishes/mlechna.jpg"}, {"name": "домати - 1 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 1/2 бр. прясна", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "репички - 5 - 6 бр.", "imageUrl": "/static/images/dishes/repichki.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "зехтин", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Измиваме хубаво зеленчуците. 1/2 краставица срязваме надлъжно на две и нарязваме. Домата белим внимателно и отрязваме капаче.", "Издълбаваме месестата част, така че да получим чашка. Репичките почистваме от листата и оформяме като цветя. В чиния слагаме в средата целия домат.", "Напълваме с млечна салата и отгоре подреждаме цветята от репички. Украсяваме с листенце магданоз.", "Около домата разпределяме изрязаната месеста част от домата и нарязаните краставички.", "Поливаме със зехтин и оцет. Посоляваме на вкус."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000021, 'Свински кебап в гювеч', '/static/images/dishes/svinski_kebeb_guvech.jpg', '["свинско месо - 1. 5 кг от плешка (може и ребърца да сложите)", "чесън - половин глава", "гъби - 250 г каквито имате", "чушки - 2 червени + 1 зелена", "лук - 7 големи глави кромид", "вино - 1 ч.ч.", "вода - 400 мл", "олио - 1 к.ч.", "сол", "черен пипер", "магданоз"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000021, 'Свински кебап в гювеч', 280, '/static/images/dishes/svinski_kebeb_guvech.jpg', '[{"name": "свинско месо - 1. 5 кг от плешка (може и ребърца да сложите)", "imageUrl": "/static/images/dishes/svisnko.jpg"}, {"name": "чесън - половин глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "гъби - 250 г каквито имате", "imageUrl": "/static/images/dishes/gubi.jpg"}, {"name": "чушки - 2 червени + 1 зелена", "imageUrl": "/static/images/dishes/chushka.jpg"}, {"name": "лук - 7 големи глави кромид", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "вино - 1 ч.ч.", "imageUrl": "/static/images/dishes/vino.jpg"}, {"name": "вода - 400 мл", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "олио - 1 к.ч.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanos.jpg"}]', '["Месото нарежете на средни по големина парчета. Лукът нарежете на шайби, а гъбите и чушките нарежете на едро. Чесънът се нарязва на филийки.", "В голям тиган в олиото запържете късчетата месо, добавете черния пипер, сол и подправките. Пържете до златист цвят. След което с решетъчна лъжица прехвърлете месото в гювеча.", "Прибавете нарязаните лук, чушки, чесън и гъби. В тигана, където пържихме месото налейте виното и водата, добавете сол и черен пипер. След като заври, оставете да ври още 5 минути и залейте месото в гювеча", "Сложете капака и поставете в затоплената фурна до 190 градуса фурна. Оставете да се готви 3. 5 часа. Отворете и добавете магданозът.", "Ако искате да се запече - махнете капака и оставете още за 20-на минутки. Ако искате може да добавите и 3-4 картофа, но той почти ще се развари (от личния опит), но пак ще е супер.", "По желание можете да добавите и други зеленчуци - моркови, грах или др.", "Получава се разкошен свински гювеч, с който всички ще си оближат пръстите."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000003, 'Таратор с копър', '/static/images/dishes/tarator.jpg', '["краставици - 2 бр.", "кисело мляко - 2 кофички", "чесън - 3 скилиди", "копър", "олио", "сол", "вода"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000003, 'Таратор с копър', 15, '/static/images/dishes/tarator.jpg', '[{"name": "краставици - 2 бр.", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "кисело мляко - 2 кофички", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "чесън - 3 скилиди", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "копър", "imageUrl": "/static/images/dishes/kopur.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "вода", "imageUrl": "/static/images/dishes/voda.jpg"}]', '["Измиваме краставиците и ги настъргваме на едро ренде. Разбиваме много добре двете кофички кисело мляко.", "Смесваме млякото с краставиците. Смачкваме чесъна и го прибавяме към таратора.", "Разбъркваме добре и доливаме вода, като количеството зависи от това колко гъст искаме да е тараторът. Накрая добавяме солта, копъра и олиото."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000024, 'Салата Тара', '/static/images/dishes/sveja.jpg', '["салата айсберг - 1 бр.", "домати - 2 бр.", "краставици - 1/2 бр. прясна", "яйца - 2 бр. твърдосварени", "ябълки - 1 бр.", "кисело мляко - 1/2 кофичка", "крутони", "слънчогледови семки"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000024, 'Салата Тара', 23, '/static/images/dishes/sveja.jpg', '[{"name": "салата айсберг - 1 бр.", "imageUrl": "/static/images/dishes/aisberg.jpg"}, {"name": "домати - 2 бр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "краставици - 1/2 бр. прясна", "imageUrl": "/static/images/dishes/krastavica.jpg"}, {"name": "яйца - 2 бр. твърдосварени", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "ябълки - 1 бр.", "imageUrl": "/static/images/dishes/qbulki.jpg"}, {"name": "кисело мляко - 1/2 кофичка", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "крутони", "imageUrl": "/static/images/dishes/krutoni.jpg"}, {"name": "слънчогледови семки", "imageUrl": "/static/images/dishes/semki.jpg"}]', '["Салатата се измива и се нарязва на ситно. На ситно се нарязват и доматите, краставиците, яйцата, и ябълките.", "Салатата се залива с киселото мляко, олио, и сол, поръсва се със слънчогледа и крутоните."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000059, 'Телешка супа', '/static/images/dishes/teleshka.jpg', '["телешко месо - 500 г", "корени - за супа", "лук - 1 - 2 глава", "картофи - 2 - 3 бр.", "сол - на вкус", "олио - 3 с.л. или 50 г масло", "черен пипер", "яйца - 1 бр.", "фиде", "лимони - сок от 1 лимон", "брашно - 1 с.л. равна"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000059, 'Телешка супа', 115, '/static/images/dishes/teleshka.jpg', '[{"name": "телешко месо - 500 г", "imageUrl": "/static/images/dishes/teleshko.jpg"}, {"name": "корени - за супа", "imageUrl": "/static/images/dishes/koreni.jpg"}, {"name": "лук - 1 - 2 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "картофи - 2 - 3 бр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "олио - 3 с.л. или 50 г масло", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "яйца - 1 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "фиде", "imageUrl": "/static/images/dishes/fide.jpg"}, {"name": "лимони - сок от 1 лимон", "imageUrl": "/static/images/dishes/limonov_sok.jpg"}, {"name": "брашно - 1 с.л. равна", "imageUrl": "/static/images/dishes/brashno.jpg"}]', '["Нарежете месото и корените за супа ситно, лука и картофите ситно.", "Смесете продуктите и ги сложете да се задушават в мазнина и малко вода.", "Поръсете с брашното, разбъркайте и долейте достатъчно гореща вода, след като съставките се поразварят, добавете зрънца черен пипер и оставете да ври до готовност на месото.", "Добавете шепа фиде и след 5 минути застройте с разбито яйце с лимонов сок. При поднасяне поръсете с магданоз.            "]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000015, 'Телешко варено класическа рецепта', '/static/images/dishes/teleshkovareno.jpg', '["олио - 1 с.л.", "телешко месо", "лук - 50 гр.", "моркови - 100 гр.", "брашно - 3 - 4 с.л.", "черен пипер - 2 г", "сол - 4 ч.л.", "магданоз - 10 гр.", "домати - 100 гр.", "картофи - 250 гр."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000015, 'Телешко варено класическа рецепта', 70, '/static/images/dishes/teleshkovareno.jpg', '[{"name": "олио - 1 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "телешко месо", "imageUrl": "/static/images/dishes/teleshko.jpg"}, {"name": "лук - 50 гр.", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "моркови - 100 гр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "брашно - 3 - 4 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "черен пипер - 2 г", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "сол - 4 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "магданоз - 10 гр.", "imageUrl": "/static/images/dishes/magdanos.jpg"}, {"name": "домати - 100 гр.", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "картофи - 250 гр.", "imageUrl": "/static/images/dishes/kartofi.jpg"}]', '["Телешките гърди се поставят в подходящ съд, заливат се с 6 чаши гореща вода, посоляват се. Добавя се корен магданоз и се вари на слаб огън, като постоянно се отпенва.", "Бульонът се прецежда, а месото се обезкостява и се нарязва на по - едри парчета.", "В горещия бульон се прибавят нарязаните на кръгчета моркови и целината, а 10 минути след като кипнат, се добавят картофите, нарязани на кубчета, доматите, срязани на четвъртинки, и обезкостеното месо.", "След сваряването на картофите яденето се смъква от огъня, добавя му се черен пипер и се посолява при нужда.", "При сервиране телешкото варено се посипва със ситно нарязан магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000065, 'Полезна тиквена крем супа', '/static/images/dishes/tikvena.jpg', '["тиква - 500 г", "картофи - 2 бр", "прясно мляко - 1 ч.ч.", "вода - 3 ч.ч.", "домати - 1/2 буркан белени", "черен пипер", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000065, 'Полезна тиквена крем супа', 70, '/static/images/dishes/tikvena.jpg', '[{"name": "тиква - 500 г", "imageUrl": "/static/images/dishes/tikva.jpg"}, {"name": "картофи - 2 бр", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "прясно мляко - 1 ч.ч.", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "вода - 3 ч.ч.", "imageUrl": "/static/images/dishes/voda.jpg"}, {"name": "домати - 1/2 буркан белени", "imageUrl": "/static/images/dishes/domaten_sos.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Почистваме, измиваме и нарязваме тиквата и картофите на кубчета.", "Прехвърляме ги в подходяща тенджера и ги заливаме с прясното мляко и 2 ч.ч. вода.", "Слагаме ги на котлона и оставяме да заври. Когато това стане добавяме доматите, солта и черния пипер.", "Намаляваме огъня и оставяме да къкри до сваряване на картофите и тиквата. При необходимост добавяме още 1 ч.ч. вода.", "Когато се сварят, снемаме от котлона тиквената супа, изчакваме 5-10 минути и пасираме."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000070, 'Тиквеник с канела', '/static/images/dishes/tikvenik.jpg', '["кори - 1 пакет за баница", "тиква - 500 - 600 г", "захар - 300 г", "олио - 200 мл", "канела - около 1 пакетче, за поръсване"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000070, 'Тиквеник с канела', 40, '/static/images/dishes/tikvenik.jpg', '[{"name": "кори - 1 пакет за баница", "imageUrl": "/static/images/dishes/kori.jpg"}, {"name": "тиква - 500 - 600 г", "imageUrl": "/static/images/dishes/tikva.jpg"}, {"name": "захар - 300 г", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "олио - 200 мл", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "канела - около 1 пакетче, за поръсване", "imageUrl": "/static/images/dishes/kanela.jpg"}]', '["Обелваме и настъргваме тиквата в купа.", "Отваряме корите и по две отделяме от тях и поръсваме с тиква, захар, олио и канела, и след това навиваме и слагаме в предварително намаслена тава в средата.", "И така подреждаме тиквеника. Отгоре го намазваме с олио и го печем в силна фурна."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000041, 'Супи с ориз и домати', '/static/images/dishes/domatena_supa.jpg', '["домати - 4 броя едри + 1/2 буркан", "ориз - 150 - 160 г", "лук - 2 глави, малки", "чесън - 2 - 3 скилидки", "моркови - 1 брой", "олио - 4 с.л.", "брашно - 1 с.л.", "магданоз - 1/2 връзка", "черен пипер - прясно смлян", "карамфил - 2 зрънца", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000041, 'Супи с ориз и домати', 45, '/static/images/dishes/domatena_supa.jpg', '[{"name": "домати - 4 броя едри + 1/2 буркан", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "ориз - 150 - 160 г", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "лук - 2 глави, малки", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "чесън - 2 - 3 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "моркови - 1 брой", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "олио - 4 с.л.", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "брашно - 1 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "магданоз - 1/2 връзка", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "черен пипер - прясно смлян", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "карамфил - 2 зрънца", "imageUrl": "/static/images/dishes/karamfil.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Накълцайте лука ситно и сложете да се задушава в сгорещената мазнина.", "Щом омекне, се добавят 2 скилидки скълцан чесън и нарязания на тънки шайби морков. След тях идва ред на почистените и ситно нарязани домати заедно с доматите от консервата.", "Оставете да се задушава на умерен котлона за около 15-20 минути и долейте гореща вода на око (няколко чаши).", "Изчакайте да покъкри за 5 минути и добавете измития ориз. След още малко сипете и размитото в малко вода брашно.", "Оставете супи с ориз и домати да се вари, докато оризът омекне. При поднасяне поръсете с малко пресен накълцан магданоз.            "]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000004, 'Доматена супа с моркови', '/static/images/dishes/tomato_soup.jpg', '["домати - 400 г, от консерва, нарязани", "моркови - 2 бр., нарязани", "лук - 1 глава, малка, на ситно нарязан", "бульон - 300 мл, зеленчуков", "риган - 1 ч.л., сушен", "индийско орехче - 1 щипка", "магданоз - 1 с.л., пресен", "дафинов лист - 1 бр. "]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000004, 'Доматена супа с моркови', 15, '/static/images/dishes/tomato_soup.jpg', '[{"name": "домати - 400 г, от консерва, нарязани", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "моркови - 2 бр., нарязани", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "лук - 1 глава, малка, на ситно нарязан", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "бульон - 300 мл, зеленчуков", "imageUrl": "/static/images/dishes/bulon.jpg"}, {"name": "риган - 1 ч.л., сушен", "imageUrl": "/static/images/dishes/rigan.jpg"}, {"name": "индийско орехче - 1 щипка", "imageUrl": "/static/images/dishes/ind_orehche.jpg"}, {"name": "магданоз - 1 с.л., пресен", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "дафинов лист - 1 бр. ", "imageUrl": "/static/images/dishes/dafinov_list.jpg"}]', '["Всички съставки се слагат да врят освен магданоза.", "След като заври, се намаля силата на огъня и се оставя да къкри около 30 мин.", "Премахва се дафиновият лист. При сервиране се гарнира с магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000011, 'Традиционна топла Пача', '/static/images/dishes/toplapacha.jpg', '["свински крачета - 3 бр.", "свински уши - 2 бр.", "свински гърди - 300 г", "морков", "кромид лук - 1 бр.", "прясно мляко - 500 мл", "чесън - 1 глава", "люти чушлета - сухи.", "сол - на вкус", "червен пипер - 2 с.л.", "черен пипер", "лют пипер - 1 к.л.", "оцет - 100 мл, винен", "брашно - 2 с.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000011, 'Традиционна топла Пача', 60, '/static/images/dishes/toplapacha.jpg', '[{"name": "свински крачета - 3 бр.", "imageUrl": "/static/images/dishes/kracheta.jpg"}, {"name": "свински уши - 2 бр.", "imageUrl": "/static/images/dishes/ushi.jpg"}, {"name": "свински гърди - 300 г", "imageUrl": "/static/images/dishes/gurdi.jpg"}, {"name": "морков", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "кромид лук - 1 бр.", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "прясно мляко - 500 мл", "imageUrl": "/static/images/dishes/prqsno_mlqko.jpg"}, {"name": "чесън - 1 глава", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "люти чушлета - сухи.", "imageUrl": "/static/images/dishes/lutachushka.jpg"}, {"name": "сол - на вкус", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "червен пипер - 2 с.л.", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "лют пипер - 1 к.л.", "imageUrl": "/static/images/dishes/piper.jpg"}, {"name": "оцет - 100 мл, винен", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "брашно - 2 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}]', '["Една от характерните български супи, особено любима на мъжете, присъствала в бита на нашия народ.", "Почистените, измити и изкиснати във вода субпродукти (уши и крачета), както и свинските гърди се заливат със студена вода. Варим на силен котлон и щом водата кипне, намаляваме температурата и прибавяме черния пипер и солта.", "Бульонът и съставките му къкрят до пълното им разваряване. Отделяме месото от костите, ушите и свинските гърди нарязваме на парченца.", "От бульона прецеждаме 1 литър. В него поставяме нарязаните на ситно лук и морков, обезкостеното и нарязано свинско месо, малко червен пипер и люта чушка. Доливаме и прясното мляко (като запазваме 50 мл от него).", "Пачата къкри на слаб котлон.", "През това време обелваме чесъна и стриваме на кашица в дървено хаванче с малко сол. Добавяме оцет и разбъркваме.", "Преди да отстраним пачата от котлона, я сгъстяваме като в 50 мл студено прясно мляко смесваме лъжица от брашното. Сервираме пачата задължително топла, гарнирана с чесновия оцет и поръсена с лют червен пипер.", "Уточнение: В рецептата присъстват продуктите - свински опашки и/или част от главата, които смених със свински гърди.", "Растителна мазнина не прибавям, защото бульонът от сварените свински вкусотии е достатъчно блажен.", "Не застройвам пачата, защото при повторното ѝ претопляне от яйцата вкусът на желето се променя (въпрос на вкус)."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000010, 'Тревненска салата', '/static/images/dishes/trevnenskasalata.jpg', '["домати", "чушки - печени червени", "лук", "чесън", "магданоз - 1/2 връзка или на вкус"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000010, 'Тревненска салата', 25, '/static/images/dishes/trevnenskasalata.jpg', '[{"name": "домати", "imageUrl": "/static/images/dishes/domati.jpg"}, {"name": "чушки - печени червени", "imageUrl": "/static/images/dishes/pecheni_chushki.jpg"}, {"name": "лук", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "чесън", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "магданоз - 1/2 връзка или на вкус", "imageUrl": "/static/images/dishes/magdanoz.jpg"}]', '["Количеството на продуктите зависи от желания от вас брой порции.", "Чушките се обелват и се подреждат цели. Доматите се нарязват на едри късове и се подреждат около чушките.", "Лукът се нарязва на кръгчета и се подрежда върху чушките.", "Чесънът се поръсва. Преди да се сервира, салатата се поръсва със ситно нарязан магданоз."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000083, 'Бърза туршия с карфиол', '/static/images/dishes/turshiq.jpg', '["карфиол", "моркови - 1 бр.", "чесън - 4 скилидки", "оцет", "захар - 2 с.л. равни", "аспирин - 1 бр.", "сол - 1 с.л. равна"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000083, 'Бърза туршия с карфиол', 10, '/static/images/dishes/turshiq.jpg', '[{"name": "карфиол", "imageUrl": "/static/images/dishes/karfiol.jpg"}, {"name": "моркови - 1 бр.", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "чесън - 4 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "оцет", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "захар - 2 с.л. равни", "imageUrl": "/static/images/dishes/zahar.jpg"}, {"name": "аспирин - 1 бр.", "imageUrl": "/static/images/dishes/aspirin.jpg"}, {"name": "сол - 1 с.л. равна", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Продуктите са за един буркан тип Омния, като според вместимостта му могат да се увеличат или намалят.", "Предварително карфиолът се измива и накъсва на розички. Морковът се обелва с резачка и нарязва на колелца.", "Скилидките чесън остават цели, като само се почиства от люспите.", "На дъното на буркана се счуква таблетката аспирин. Налива се оцет до долния ръб на буркана.", "Прибавят се солта и захарта и всичко се разбърква.", "В буркана се нареждат розичките карфиол, като между тях се поставят колелцата морков и скилидките чесън.", "Накрая в буркана се налива вода и се затваря плътно.", "Разклаща се и се обръща с капачката надолу за един ден.", "На следващия ден домашната туршия се обръща с капачката нагоре.", "Така се процедира в продължение на десет дни, след което туршията с карфиол е готова за консумация.", "Ако няма да я хапвате веднага, бързата туршия с карфиол се прибира на хладно и тъмно място."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000076, 'Домашна юфка с крема сирене и сирене', '/static/images/dishes/ufkas.jpg', '["юфка - 1 пакет домашна", "сирене - 50 г", "крема сирене - 30 г.", "олио", "сол - 1 ч.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000076, 'Домашна юфка с крема сирене и сирене', 10, '/static/images/dishes/ufkas.jpg', '[{"name": "юфка - 1 пакет домашна", "imageUrl": "/static/images/dishes/mlqko.jpg"}, {"name": "сирене - 50 г", "imageUrl": "/static/images/dishes/sirene.jpg"}, {"name": "крема сирене - 30 г.", "imageUrl": "/static/images/dishes/krema_sirene.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол - 1 ч.л.", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Добавя се вода в тенджера с две три капки олио и се оставя да кипне.", "Добавя се юфката във врящата вода и се вари 2-3 мин.", "Добавя се крема сиренето и сиренето и се разбърква.", "Добавя се сол на вкус. По желание се сервира с още сирене."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000020, 'Коледен венец от руска салата', '/static/images/dishes/venec-ruska-salata.jpg', '["руска салата - 1 кутия 200 г готова", "копър - 3 стръка", "кашкавал - 100гр.", "чушки - 1 червена маринована чушка"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000020, 'Коледен венец от руска салата', 20, '/static/images/dishes/venec-ruska-salata.jpg', '[{"name": "руска салата - 1 кутия 200 г готова", "imageUrl": "/static/images/dishes/russian_salad.jpg"}, {"name": "копър - 3 стръка", "imageUrl": "/static/images/dishes/kopur.jpg"}, {"name": "кашкавал - 100гр.", "imageUrl": "/static/images/dishes/kashkaval.jpg"}, {"name": "чушки - 1 червена маринована чушка", "imageUrl": "/static/images/dishes/chushki.jpg"}]', '["В плитка чиния слагаме руската салата и я оформяме на равен кръг с дебелина 1 сантиметър.", "Подреждаме копъра около салатата, да оформим венеца.", "От една пръчица кашкавал с дебелина 2 см и дължина 8 см оформяме свещ в долния край на венеца.", "От червената чушка изрязваме пламъка на свещта, както и панделка."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (2000012, 'Класически винен кебап', '/static/images/dishes/vinenkebab.jpg', '["лук - 80 г", "свинско месо - 500 г", "ориз - 100 г", "олио", "брашно - 3 - 4 с.л.", "черен пипер - 2 г", "сол", "доматено пюре - 1 с.л.", "червено вино - 50 мл", "червен пипер - 1/2 с.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (2000012, 'Класически винен кебап', 70, '/static/images/dishes/vinenkebab.jpg', '[{"name": "лук - 80 г", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "свинско месо - 500 г", "imageUrl": "/static/images/dishes/svinsko.jpg"}, {"name": "ориз - 100 г", "imageUrl": "/static/images/dishes/oriz.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "брашно - 3 - 4 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "черен пипер - 2 г", "imageUrl": "/static/images/dishes/cheren_piper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "доматено пюре - 1 с.л.", "imageUrl": "/static/images/dishes/pure.jpg"}, {"name": "червено вино - 50 мл", "imageUrl": "/static/images/dishes/cherveno_vino.jpg"}, {"name": "червен пипер - 1/2 с.л.", "imageUrl": "/static/images/dishes/cherven_piper.jpg"}]', '["Месото се надребнява на късчета, по около 30 г всяко. Поръсва се със солта, черния млян пипер. Прибавя се скълцаният лук. Мазнината се загрява и в нея се слага месото с лука.", "Готви се до редуцирането на водата от лука. Прибавят се червен пипер, разреденото доматено пюре и половината вино.", "Задушава се на тих огън, докато остане на мазнина. Залива се с малко горещ бульон или вода. Щом омекне месото, се слагат останалото вино, дафиновият лист, зърната пипер и запърженото в маслото брашно.", "Тенджерата се слага в загрята на 180 градуса фурна и се пече до готовността на месото.", "Виненият кебап се сервират със ориз, задушен в масло."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000094, 'Салата за зимата в буркани', '/static/images/dishes/zimna.jpg', '["корнишони - 2 кг", "оцет - 150 г", "олио - 150 г", "сол - 3 с.л.", "чушки - 1 кг", "чесън - 4 - 5 скилидки", "копър - връзка"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000094, 'Салата за зимата в буркани', 100, '/static/images/dishes/zimna.jpg', '[{"name": "корнишони - 2 кг", "imageUrl": "/static/images/dishes/kornishoni.jpg"}, {"name": "оцет - 150 г", "imageUrl": "/static/images/dishes/ocet.jpg"}, {"name": "олио - 150 г", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "сол - 3 с.л.", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "чушки - 1 кг", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "чесън - 4 - 5 скилидки", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "копър - връзка", "imageUrl": "/static/images/dishes/kopur.jpg"}]', '["Изпечени и обелени чушките се режат на парчета, корнишоните също се нарязват на колелца.", "Добавя се олиото, оцета, солта и захарта, нарязаните чесън и копър. Всичко се разбърква много добре и се оставя да престои 3-4 часа.", "Сипва се буркани, които се стерилизират за 10 минути."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000063, 'Заешка супа', '/static/images/dishes/zaeshaka.jpg', '["заешко - 1 кош заедно с шията от заек + дреболиите", "брашно - 2 - 3 с.л.", "чушка - 1 бр.", "чесън - 1 скилидка", "лук - 1 глава", "картоф - 1 голям", "целина - парче от глава", "фиде - около шепа", "яйце - 1 бр.", "сметана - 1 с.л.", "риган", "мащерка", "магданоз", "черен пипер", "сол"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000063, 'Заешка супа', 100, '/static/images/dishes/zaeshaka.jpg', '[{"name": "заешко - 1 кош заедно с шията от заек + дреболиите", "imageUrl": "/static/images/dishes/zaek.jpg"}, {"name": "брашно - 2 - 3 с.л.", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "чушка - 1 бр.", "imageUrl": "/static/images/dishes/chushki.jpg"}, {"name": "чесън - 1 скилидка", "imageUrl": "/static/images/dishes/chesun.jpg"}, {"name": "лук - 1 глава", "imageUrl": "/static/images/dishes/luk.jpg"}, {"name": "картоф - 1 голям", "imageUrl": "/static/images/dishes/kartofi.jpg"}, {"name": "целина - парче от глава", "imageUrl": "/static/images/dishes/celina.jpg"}, {"name": "фиде - около шепа", "imageUrl": "/static/images/dishes/fide.jpg"}, {"name": "яйце - 1 бр.", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "сметана - 1 с.л.", "imageUrl": "/static/images/dishes/smetana.jpg"}, {"name": "риган", "imageUrl": "/static/images/dishes/rigan.jpg"}, {"name": "мащерка", "imageUrl": "/static/images/dishes/mashterka.jpg"}, {"name": "магданоз", "imageUrl": "/static/images/dishes/magdanoz.jpg"}, {"name": "черен пипер", "imageUrl": "/static/images/dishes/black_paper.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}]', '["Отделения кош с шията и дреболийките слагаме в тенджера под налягане в посолена вода и варим след сигнализацията за начало на процеса около 20 минутки.", "Лука нарязваме на ситно, чесъна също и задушаваме със зехтин в тенджерата, в която ще приготвяме супата. Добавяме риган и сол, а преди да стане готова запръжката, добавяме брашното и енергично бъркаме.", "Междувременно готовото месо изваждаме от бульона, а него прецеждаме и изсипваме при омекналия лук. Добавяме останалите ситно нарязани продукти, черен пипер, мащерка и малко сол, допълваме с вода, докато достигне общ обем около 4 л, и похлупваме.", "Старателно обезкостяваме месото, накъсваме го ситно, а дреболийките нарязваме и прибавяме към супата. Когато всичко е готово, слагаме фидето и подготвяме застройката от яйцето, сметаната и магданоза.", "След като фидето набъбне, сваляме супата от котлона, изчакваме малко и застройваме. Ако е необходимо, се добавя сол, според вкуса."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000045, 'Застройка за супи', '/static/images/dishes/zastroika.jpg', '["яйца - 1 брой", "брашно - 1 с.л. равна", "кисело мляко - 1 ч.ч.", "оцет - 1 с.л."]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000045, 'Застройка за супи', 5, '/static/images/dishes/zastroika.jpg', '[{"name": "яйца - 1 брой", "imageUrl": "/static/images/dishes/eggs.jpg"}, {"name": "брашно - 1 с.л. равна", "imageUrl": "/static/images/dishes/brashno.jpg"}, {"name": "кисело мляко - 1 ч.ч.", "imageUrl": "/static/images/dishes/kiselo_mlqko.jpg"}, {"name": "оцет - 1 с.л.", "imageUrl": "/static/images/dishes/ocet.jpg"}]', '["Яйцата с оцета се разбиват заедно с брашното и киселото мляко до гладка смес.", "Гребва се черпак от горещата супа и внимателно се сипва при застройката, за да се темперират двете смеси.", "Добавят се още 2-3 черпака и се бърка, след което всичко се излива в супата, като се бърка."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000098, 'Кисело зеле в буркани', '/static/images/dishes/kiselozele.jpg', '["зеле", "моркови", "сол", "целина"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000098, 'Кисело зеле в буркани', 120, '/static/images/dishes/kiselozele.jpg', '[{"name": "зеле", "imageUrl": "/static/images/dishes/zele.jpg"}, {"name": "моркови", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "сол", "imageUrl": "/static/images/dishes/sol.jpg"}, {"name": "целина", "imageUrl": "/static/images/dishes/celina.jpg"}]', '["Количеството на продуктите зависи от това колко буркана искате да напълните. Необходими са ви чисти големи буркани.", "Първо почистете зелките. За целта откъснете горните два реда листа на всяка зелка, измийте ги добре и ги сложете цели на дъното на бурканите.", "Останалото зеле се нарязва и се разбърква с настъргани моркови, както и с накълцани стръкове целина.", "Претрийте зелето със сол и го натъпчете в бурканите до гърлото им. Поставете по един камък за тежест в бурканите и залейте с леко осолена хладка вода, която трябва да покрива зелето един пръст.", "Сложете бурканите в кухнята на топло, а гърлата им завийте с двойно сгъната марля и ластик. След 4-5 дни киселото зеле трябва да е годно за консумация."]');
INSERT IGNORE INTO dishes (id, title, imageUrl, ingredients) VALUES (1000028, 'Зелева салата', '/static/images/dishes/zeleva.jpg', '["кисело зеле - 1 глава", "олио", "моркови - 1 бр. от туршия", "червен пипер"]');
INSERT IGNORE INTO details (id, title, readyInMinutes, imageUrl, ingredients, instructions) VALUES (1000028, 'Зелева салата', 10, '/static/images/dishes/zeleva.jpg', '[{"name": "кисело зеле - 1 глава", "imageUrl": "/static/images/dishes/kiselo_zele.jpg"}, {"name": "олио", "imageUrl": "/static/images/dishes/olio.jpg"}, {"name": "моркови - 1 бр. от туршия", "imageUrl": "/static/images/dishes/morkovi.jpg"}, {"name": "червен пипер", "imageUrl": "/static/images/dishes/red_paper.jpg"}]', '["Нарязва се зелето.", "Рендосва се морковът и се овкусява с олио и червен пипер."]');
